static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;
  FILE *logf = fopen("/tmp/logs/4203.txt", "a");

  /* Minimal entrance log */
  if (logf)
  {
    fprintf(logf, "ENTER transmit_error_response_len: connection=%p, status_code=%u, message=%p, message_len=%zu, header_name=%p, header_name_len=%zu, header_value=%p, header_value_len=%zu\n",
            (void *)connection, status_code, (const void *)message, message_len,
            (void *)header_name, header_name_len, (void *)header_value, header_value_len);
    fflush(logf);
  }

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (connection->stop_with_error)
  { /* Should not happen */
    if (logf)
    {
      fprintf(logf, "EARLY RETURN: stop_with_error already set. connection->state=%d\n", connection->state);
      fflush(logf);
    }
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    if (logf) fclose(logf);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif

  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    if (logf)
    {
      fprintf(logf, "TOO LATE: connection->state=%d\n", connection->state);
      fflush(logf);
    }
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    free (header_value);
    if (logf) fclose(logf);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
  }

  /* Probe: record message buffer properties before creating response */
  if (logf)
  {
    fprintf(logf, "BEFORE create_response: message=%p, message_len=%zu\n",
            (const void *)message, message_len);
    if (message == NULL)
    {
      fprintf(logf, "  message is NULL\n");
    }
    else
    {
      size_t i, to = message_len;
      /* limit dump to avoid excessive logs */
      if (to > 64) to = 64;
      fprintf(logf, "  dumping up to %zu bytes of message (no assumption of NUL terminator):\n", to);
      for (i = 0; i < to; ++i)
      {
        unsigned char c = (unsigned char)message[i];
        /* printable ascii check */
        char pc = (c >= 0x20 && c <= 0x7e) ? (char)c : '.';
        fprintf(logf, " %02x(%c)", c, pc);
      }
      if (message_len > to)
        fprintf(logf, " ... (truncated)\n");
      else
        fprintf(logf, "\n");

      /* Check for presence of NUL within message_len */
      {
        size_t i;
        int found = 0;
        for (i = 0; i < message_len; ++i)
        {
          if (message[i] == '\0')
          {
            found = 1;
            break;
          }
        }
        fprintf(logf, "  NUL found within message_len: %s (index=%zu if yes)\n",
                found ? "yes" : "no", found ? i : (size_t)-1);
      }
    }
    fprintf(logf, "  header_name=%p header_name_len=%zu header_value=%p header_value_len=%zu\n",
            (void *)header_name, header_name_len, (void *)header_value, header_value_len);
    fflush(logf);
  }

  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (logf)
  {
    fprintf(logf, "AFTER create_response: response=%p\n", (void *)response);
    fflush(logf);
  }
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    if (logf)
    {
      fprintf(logf, "CREATE_RESPONSE FAILED: closing connection state -> CLOSED\n");
      fflush(logf);
    }
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    if (logf) fclose(logf);
    return;
  }
  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));
  if (NULL != header_name)
  {
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (MHD_NO == iret)
    {
      if (logf)
      {
        fprintf(logf, "MHD_add_response_entry_no_alloc_ returned NO, freeing headers.\n");
        fflush(logf);
      }
      free (header_name);
      free (header_value);
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    if (logf)
    {
      fprintf(logf, "QUEUING response: status_code=%u, connection->in_access_handler set to true\n", status_code);
      fflush(logf);
    }
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;
    if (logf)
    {
      fprintf(logf, "AFTER queue_response: iret=%d, connection->in_access_handler restored to %d\n", iret, connection->in_access_handler);
      fflush(logf);
    }
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    if (logf)
    {
      fprintf(logf, "FAILED to queue response -> closed connection.\n");
      fflush(logf);
      fclose(logf);
    }
    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    if (logf)
    {
      fprintf(logf, "build_header_response failed first attempt. Releasing resources and retrying with empty buffer.\n");
      fflush(logf);
    }
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      if (logf)
      {
        fprintf(logf, "build_header_response failed second attempt -> closing connection.\n");
        fflush(logf);
        fclose(logf);
      }
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;
  if (logf)
  {
    fprintf(logf, "EXIT transmit_error_response_len: connection->state=%d, keepalive=%d\n", connection->state, connection->keepalive);
    fflush(logf);
    fclose(logf);
  }
}
