static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;

  /* Open a log file for probes. Use append so multiple calls accumulate evidence. */
  FILE *log = fopen("/tmp/logs/4205.txt", "a");
  if (log)
  {
    fprintf(log, "--- transmit_error_response_len ENTER ---\n");
    fprintf(log, "connection=%p state=%d stop_with_error=%d discard_request=%d\n",
            (void *)connection,
            (int)connection->state,
            (int)connection->stop_with_error,
            (int)connection->discard_request);
    fprintf(log, "status_code=%u, message=%p, message_len=%zu\n",
            status_code, (const void *)message, message_len);
    fprintf(log, "header_name=%p, header_name_len=%zu, header_value=%p, header_value_len=%zu\n",
            (void *)header_name, header_name_len,
            (void *)header_value, header_value_len);
    /* If header_name/header_value are present, print up to first 64 bytes as hex for evidence. */
    if (header_name != NULL && header_name_len > 0)
    {
      size_t lim = (header_name_len < 64) ? header_name_len : 64;
      fprintf(log, "header_name (hex up to %zu bytes):",
              lim);
      for (size_t i = 0; i < lim; ++i)
        fprintf(log, " %02x", (unsigned char)header_name[i]);
      fprintf(log, "\n");
    }
    if (header_value != NULL && header_value_len > 0)
    {
      size_t limv = (header_value_len < 64) ? header_value_len : 64;
      fprintf(log, "header_value (hex up to %zu bytes):",
              limv);
      for (size_t i = 0; i < limv; ++i)
        fprintf(log, " %02x", (unsigned char)header_value[i]);
      fprintf(log, "\n");
    }
    fflush(log);
  }

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (connection->stop_with_error)
  { /* Should not happen */
    if (log)
      fprintf(log, "Early exit: connection->stop_with_error true. Will free header buffers and return.\n");
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    if (log)
    {
      fprintf(log, "Freeing header_name=%p and header_value=%p\n", (void *)header_name, (void *)header_value);
      fflush(log);
    }
    free (header_name);
    free (header_value);
    if (log)
    {
      fprintf(log, "Exiting transmit_error_response_len (stop_with_error path)\n---\n");
      fclose(log);
    }
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif
  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    if (log)
    {
      fprintf(log, "Too late to send error response (state=%d). Will close connection and free headers.\n", (int)connection->state);
      fprintf(log, "Freeing header_name=%p and header_value=%p\n", (void *)header_name, (void *)header_value);
      fflush(log);
    }
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    free (header_value);
    if (log)
    {
      fprintf(log, "Exiting transmit_error_response_len (too-late path)\n---\n");
      fclose(log);
    }
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
    if (log)
    {
      fprintf(log, "Dropped read buffer to make space for error response.\n");
      fflush(log);
    }
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
    if (log)
    {
      fprintf(log, "Destroyed existing connection->rp.response.\n");
      fflush(log);
    }
  }
  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    if (log)
    {
      fprintf(log, "MHD_create_response_from_buffer_static returned NULL (no memory).\n");
      fprintf(log, "Freeing header_name=%p and header_value=%p and closing connection.\n",
              (void *)header_name, (void *)header_value);
      fflush(log);
    }
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    free (header_value);
    if (log)
    {
      fprintf(log, "Exiting transmit_error_response_len (create response failure)\n---\n");
      fclose(log);
    }
    return;
  }
  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));
  if (NULL != header_name)
  {
    if (log)
    {
      fprintf(log, "Calling MHD_add_response_entry_no_alloc_ with header_name=%p (len=%zu) header_value=%p (len=%zu)\n",
              (void *)header_name, header_name_len, (void *)header_value, header_value_len);
      fflush(log);
    }
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (MHD_NO == iret)
    {
      if (log)
      {
        fprintf(log, "MHD_add_response_entry_no_alloc_ returned MHD_NO. Freeing header buffers.\n");
        fflush(log);
      }
      free (header_name);
      free (header_value);
    }
  }
  else
    iret = MHD_YES;

  if (log)
  {
    fprintf(log, "Result of adding header: iret=%d\n", (int)iret);
    fflush(log);
  }

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    if (log)
    {
      fprintf(log, "Queuing response: status_code=%u, response=%p (before MHD_queue_response)\n",
              status_code, (void *)response);
      fflush(log);
    }
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;
    if (log)
    {
      fprintf(log, "After MHD_queue_response: iret=%d, connection->rp.response=%p\n", (int)iret, (void *)connection->rp.response);
      fflush(log);
    }
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    if (log)
    {
      fprintf(log, "Failed to queue response; closing connection. Exiting.\n---\n");
      fclose(log);
    }
    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    if (log)
    {
      fprintf(log, "build_header_response failed first time. Reset buffers and retrying.\n");
      fflush(log);
    }

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      if (log)
      {
        fprintf(log, "build_header_response failed second time. Closing connection.\n---\n");
        fclose(log);
      }
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;

  if (log)
  {
    fprintf(log, "Exiting transmit_error_response_len normally. state=%d keepalive=%d\n---\n",
            (int)connection->state, (int)connection->keepalive);
    fclose(log);
  }
}
