static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;
  FILE *log = fopen("/tmp/logs/4205.txt", "a");

  if (log)
  {
    fprintf(log, "ENTER transmit_error_response_len: connection=%p status_code=%u message=%p message_len=%zu header_name=%p header_name_len=%zu header_value=%p header_value_len=%zu state=%d stop_with_error=%d\n",
            (void *)connection,
            status_code,
            (const void *)message,
            message_len,
            (void *)header_name,
            header_name_len,
            (void *)header_value,
            header_value_len,
            connection ? (int)connection->state : -1,
            connection ? (int)connection->stop_with_error : -1);
    if (message && message_len > 0)
    {
      fprintf(log, "message (first up to 256 bytes or full length %zu):\n", message_len);
      fwrite(message, 1, message_len > 256 ? 256 : message_len, log);
      fprintf(log, "\n---end message preview---\n");
    }
    fflush(log);
  }

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (connection->stop_with_error)
  { /* Should not happen */
    if (log)
    {
      fprintf(log, "EARLY RETURN: stop_with_error set. connection->state=%d\n",
              (int)connection->state);
      fflush(log);
    }
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    if (log)
    {
      fprintf(log, "free(header_name) called (early-exit). header_name=%p\n", (void *)header_name);
      fflush(log);
    }
    free (header_value);
    if (log)
    {
      fprintf(log, "free(header_value) called (early-exit). header_value=%p\n", (void *)header_value);
      fflush(log);
    }
    if (log) fclose(log);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif
  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    if (log)
    {
      fprintf(log, "Too late to send an error response: connection->state=%d\n", (int)connection->state);
      fflush(log);
    }
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    free (header_name);
    if (log)
    {
      fprintf(log, "free(header_name) called (too-late). header_name=%p\n", (void *)header_name);
      fflush(log);
    }
    free (header_value);
    if (log)
    {
      fprintf(log, "free(header_value) called (too-late). header_value=%p\n", (void *)header_value);
      fflush(log);
    }
    if (log) fclose(log);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    if (log)
    {
      fprintf(log, "Discarding read_buffer: read_buffer=%p read_buffer_size=%zu\n",
              (void *)connection->read_buffer,
              connection->read_buffer_size);
      fflush(log);
    }
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
  }
  if (NULL != connection->rp.response)
  {
    if (log)
    {
      fprintf(log, "Destroying existing response: rp.response=%p\n", (void *)connection->rp.response);
      fflush(log);
    }
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
  }
  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    if (log)
    {
      fprintf(log, "Failed to create response (NULL). Closing connection.\n");
      fflush(log);
    }
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    free (header_name);
    if (log)
    {
      fprintf(log, "free(header_name) called (response-null). header_name=%p\n", (void *)header_name);
      fflush(log);
    }
    free (header_value);
    if (log)
    {
      fprintf(log, "free(header_value) called (response-null). header_value=%p\n", (void *)header_value);
      fflush(log);
    }
    if (log) fclose(log);
    return;
  }
  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));
  if (NULL != header_name)
  {
    if (log)
    {
      fprintf(log, "Adding response header: header_name=%p header_name_len=%zu header_value=%p header_value_len=%zu\n",
              (void *)header_name,
              header_name_len,
              (void *)header_value,
              header_value_len);
      if (header_name_len > 0)
      {
        fprintf(log, "header_name (preview up to 256 bytes):\n");
        fwrite(header_name, 1, header_name_len > 256 ? 256 : header_name_len, log);
        fprintf(log, "\n---end header_name preview---\n");
      }
      if (header_value_len > 0)
      {
        fprintf(log, "header_value (preview up to 256 bytes):\n");
        fwrite(header_value, 1, header_value_len > 256 ? 256 : header_value_len, log);
        fprintf(log, "\n---end header_value preview---\n");
      }
      fflush(log);
    }

    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (MHD_NO == iret)
    {
      if (log)
      {
        fprintf(log, "MHD_add_response_entry_no_alloc_ returned MHD_NO. freeing header_name and header_value.\n");
        fflush(log);
      }
      free (header_name);
      free (header_value);
      if (log)
      {
        fprintf(log, "free(header_name) and free(header_value) done after add_response failure.\n");
        fflush(log);
      }
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    if (log)
    {
      fprintf(log, "Queueing response: connection=%p status_code=%u response=%p before_in_access_handler=%d\n",
              (void *)connection, status_code, (void *)response, (int)before);
      fflush(log);
    }

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;

    if (log)
    {
      fprintf(log, "MHD_queue_response returned %d, restored in_access_handler=%d\n",
              (int)iret, (int)connection->in_access_handler);
      fflush(log);
    }
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    if (log)
    {
      fprintf(log, "Failed to queue response, closed connection.\n");
      fflush(log);
      fclose(log);
    }
    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    if (log)
    {
      fprintf(log, "build_header_response failed first attempt. Clearing various request fields and retrying.\n");
      fflush(log);
    }
    /* No memory. Release everything. */
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      if (log)
      {
        fprintf(log, "build_header_response failed second attempt. Closing connection.\n");
        fflush(log);
        fclose(log);
      }
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;
  if (log)
  {
    fprintf(log, "EXIT transmit_error_response_len: final state=%d keepalive=%d rp.response=%p\n",
            (int)connection->state, (int)connection->keepalive, (void *)connection->rp.response);
    fflush(log);
    fclose(log);
  }
}
