static void
transmit_error_response_len (struct MHD_Connection *connection,
                             unsigned int status_code,
                             const char *message,
                             size_t message_len,
                             char *header_name,
                             size_t header_name_len,
                             char *header_value,
                             size_t header_value_len)
{
  struct MHD_Response *response;
  enum MHD_Result iret;
  FILE *fp = fopen("/tmp/logs/4205.txt", "a");

  if (fp)
  {
    fprintf(fp, "ENTER transmit_error_response_len: connection=%p status=%u message_ptr=%p message_len=%zu header_name=%p header_name_len=%zu header_value=%p header_value_len=%zu\n",
            (void *) connection,
            status_code,
            (const void *) message,
            message_len,
            (void *) header_name,
            header_name_len,
            (void *) header_value,
            header_value_len);
    if (message != NULL && message_len > 0)
    {
      fprintf(fp, "message contents (len=%zu): '", message_len);
      fwrite(message, 1, message_len, fp);
      fprintf(fp, "'\n");
    }
    fflush(fp);
  }

  mhd_assert (! connection->stop_with_error); /* Do not send error twice */
  if (fp)
  {
    fprintf(fp, "check: connection->stop_with_error=%d connection->state=%d\n",
            (int) connection->stop_with_error,
            (int) connection->state);
    fflush(fp);
  }

  if (connection->stop_with_error)
  { /* Should not happen */
    if (fp)
    {
      fprintf(fp, "early return: stop_with_error set; closing connection.\n");
      fflush(fp);
    }
    if (MHD_CONNECTION_CLOSED > connection->state)
      connection->state = MHD_CONNECTION_CLOSED;
    if (fp)
    {
      fprintf(fp, "freeing header_name=%p header_value=%p before return\n",
              (void *) header_name,
              (void *) header_value);
      fflush(fp);
    }
    free (header_name);
    free (header_value);
    if (fp) fclose(fp);
    return;
  }
  connection->stop_with_error = true;
  connection->discard_request = true;
#ifdef HAVE_MESSAGES
  MHD_DLOG (connection->daemon,
            _ ("Error processing request (HTTP response code is %u ('%s')). " \
               "Closing connection.\n"),
            status_code,
            message);
#endif
  if (MHD_CONNECTION_START_REPLY < connection->state)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Too late to send an error response, " \
                 "response is being sent already.\n"),
              status_code,
              message);
#endif
    if (fp)
    {
      fprintf(fp, "Too late to send error response: connection->state=%d\n",
              (int) connection->state);
      fflush(fp);
    }
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Too late for error response."));
    if (fp)
    {
      fprintf(fp, "freeing header_name=%p header_value=%p before return (too late)\n",
              (void *) header_name,
              (void *) header_value);
      fflush(fp);
    }
    free (header_name);
    free (header_value);
    if (fp) fclose(fp);
    return;
  }
  /* TODO: remove when special error queue function is implemented */
  connection->state = MHD_CONNECTION_FULL_REQ_RECEIVED;
  if (0 != connection->read_buffer_size)
  {
    /* Read buffer is not needed anymore, discard it
     * to free some space for error response. */
    MHD_pool_deallocate (connection->pool,
                         connection->read_buffer,
                         connection->read_buffer_size);
    connection->read_buffer = NULL;
    connection->read_buffer_size = 0;
    connection->read_buffer_offset = 0;
    if (fp)
    {
      fprintf(fp, "cleared read buffer; read_buffer_size now 0\n");
      fflush(fp);
    }
  }
  if (NULL != connection->rp.response)
  {
    MHD_destroy_response (connection->rp.response);
    connection->rp.response = NULL;
    if (fp)
    {
      fprintf(fp, "destroyed existing response\n");
      fflush(fp);
    }
  }
  response = MHD_create_response_from_buffer_static (message_len,
                                                     message);
  if (NULL == response)
  {
#ifdef HAVE_MESSAGES
    MHD_DLOG (connection->daemon,
              _ ("Failed to create error response.\n"),
              status_code,
              message);
#endif
    if (fp)
    {
      fprintf(fp, "MHD_create_response_from_buffer_static returned NULL\n");
      fflush(fp);
    }
    /* can't even send a reply, at least close the connection */
    connection->state = MHD_CONNECTION_CLOSED;
    if (fp)
    {
      fprintf(fp, "freeing header_name=%p header_value=%p before return (no response)\n",
              (void *) header_name,
              (void *) header_value);
      fflush(fp);
    }
    free (header_name);
    free (header_value);
    if (fp) fclose(fp);
    return;
  }
  mhd_assert ((0 == header_name_len) || (NULL != header_name));
  mhd_assert ((NULL == header_name) || (0 != header_name_len));
  mhd_assert ((0 == header_value_len) || (NULL != header_value));
  mhd_assert ((NULL == header_value) || (0 != header_value_len));
  mhd_assert ((NULL == header_name) || (NULL != header_value));
  mhd_assert ((NULL != header_value) || (NULL == header_name));

  if (fp)
  {
    fprintf(fp, "assertions passed for header_name/header_value\n");
    if (header_name != NULL)
    {
      fprintf(fp, "header_name (len=%zu): '", header_name_len);
      fwrite(header_name, 1, header_name_len, fp);
      fprintf(fp, "'\n");
      fprintf(fp, "header_value (len=%zu): '", header_value_len);
      fwrite(header_value, 1, header_value_len, fp);
      fprintf(fp, "'\n");
    }
    fflush(fp);
  }

  if (NULL != header_name)
  {
    iret = MHD_add_response_entry_no_alloc_ (response,
                                             MHD_HEADER_KIND,
                                             header_name, header_name_len,
                                             header_value, header_value_len);
    if (fp)
    {
      fprintf(fp, "called MHD_add_response_entry_no_alloc_ -> iret=%d\n", (int) iret);
      fflush(fp);
    }
    if (MHD_NO == iret)
    {
      if (fp)
      {
        fprintf(fp, "MHD_add_response_entry_no_alloc_ failed: freeing header_name/value\n");
        fflush(fp);
      }
      free (header_name);
      free (header_value);
    }
  }
  else
    iret = MHD_YES;

  if (MHD_NO != iret)
  {
    bool before = connection->in_access_handler;

    if (fp)
    {
      fprintf(fp, "about to queue response; in_access_handler before=%d\n", (int) before);
      fflush(fp);
    }

    /* Fake the flag for the internal call */
    connection->in_access_handler = true;
    iret = MHD_queue_response (connection,
                               status_code,
                               response);
    connection->in_access_handler = before;

    if (fp)
    {
      fprintf(fp, "MHD_queue_response returned iret=%d\n", (int) iret);
      fflush(fp);
    }
  }
  MHD_destroy_response (response);
  if (MHD_NO == iret)
  {
    /* can't even send a reply, at least close the connection */
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection " \
                               "(failed to queue error response)."));
    if (fp)
    {
      fprintf(fp, "failed to queue response; closing connection.\n");
      fflush(fp);
      fclose(fp);
    }
    return;
  }
  mhd_assert (NULL != connection->rp.response);
  /* Do not reuse this connection. */
  connection->keepalive = MHD_CONN_MUST_CLOSE;
  if (MHD_NO == build_header_response (connection))
  {
    /* No memory. Release everything. */
    if (fp)
    {
      fprintf(fp, "build_header_response failed first attempt; releasing resources and retrying with empty buffer\n");
      fflush(fp);
    }
    connection->rq.version = NULL;
    connection->rq.method = NULL;
    connection->rq.url = NULL;
    connection->rq.url_len = 0;
    connection->rq.headers_received = NULL;
    connection->rq.headers_received_tail = NULL;
    connection->write_buffer = NULL;
    connection->write_buffer_size = 0;
    connection->write_buffer_send_offset = 0;
    connection->write_buffer_append_offset = 0;
    connection->read_buffer
      = MHD_pool_reset (connection->pool,
                        NULL,
                        0,
                        0);
    connection->read_buffer_size = 0;

    /* Retry with empty buffer */
    if (MHD_NO == build_header_response (connection))
    {
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection " \
                                 "(failed to create error response header)."));
      if (fp)
      {
        fprintf(fp, "build_header_response failed second attempt; closing connection\n");
        fflush(fp);
        fclose(fp);
      }
      return;
    }
  }
  connection->state = MHD_CONNECTION_HEADERS_SENDING;

  if (fp)
  {
    fprintf(fp, "exiting transmit_error_response_len: state set to MHD_CONNECTION_HEADERS_SENDING\n");
    fflush(fp);
    fclose(fp);
  }
}
