static bool
try_grow_read_buffer (struct MHD_Connection *connection,
                      bool required)
{
  size_t new_size;
  size_t avail_size;
  const size_t def_grow_size = connection->daemon->pool_increment;
  void *rb;
  FILE *logf = fopen("/tmp/logs/308.txt", "a");

  if (NULL != logf)
  {
    fprintf(logf, "ENTER try_grow_read_buffer: connection=%p, read_buffer=%p, read_buffer_size=%zu, read_buffer_offset=%zu, required=%d, pool_increment=%zu\n",
            (void *)connection,
            connection ? connection->read_buffer : NULL,
            connection ? connection->read_buffer_size : 0,
            connection ? connection->read_buffer_offset : 0,
            required,
            def_grow_size);
    fflush(logf);
  }

  avail_size = MHD_pool_get_free (connection->pool);
  if (NULL != logf)
  {
    fprintf(logf, "avail_size=%zu\n", avail_size);
    fflush(logf);
  }
  if (0 == avail_size)
  {
    if (NULL != logf)
    {
      fprintf(logf, "No more space available -> return false\n");
      fclose(logf);
    }
    return false;               /* No more space available */
  }
  if (0 == connection->read_buffer_size)
    new_size = avail_size / 2;  /* Use half of available buffer for reading */
  else
  {
    size_t grow_size;

    grow_size = avail_size / 8;
    if (def_grow_size > grow_size)
    {                  /* Shortage of space */
      const size_t left_free =
        connection->read_buffer_size - connection->read_buffer_offset;
      mhd_assert (connection->read_buffer_size >= \
                  connection->read_buffer_offset);
      if ((def_grow_size <= grow_size + left_free)
          && (left_free < def_grow_size))
        grow_size = def_grow_size - left_free;  /* Use precise 'def_grow_size' for new free space */
      else if (! required)
      {
        if (NULL != logf)
        {
          fprintf(logf, "Grow not mandatory and insufficient space -> return false (grow_size=%zu, left_free=%zu, def_grow_size=%zu)\n",
                  grow_size, left_free, def_grow_size);
          fclose(logf);
        }
        return false;                           /* Grow is not mandatory, leave some space in pool */
      }
      else
      {
        /* Shortage of space, but grow is mandatory */
        const size_t small_inc =
          ((MHD_BUF_INC_SIZE > def_grow_size) ?
           def_grow_size : MHD_BUF_INC_SIZE) / 8;
        if (small_inc < avail_size)
          grow_size = small_inc;
        else
          grow_size = avail_size;
      }
    }
    new_size = connection->read_buffer_size + grow_size;
  }
  if (NULL != logf)
  {
    fprintf(logf, "Computed new_size=%zu\n", new_size);
    fflush(logf);
  }
  /* Make sure that read buffer will not be moved */
  if ((NULL != connection->read_buffer) &&
      ! MHD_pool_is_resizable_inplace (connection->pool,
                                       connection->read_buffer,
                                       connection->read_buffer_size))
  {
    if (NULL != logf)
    {
      fprintf(logf, "Read buffer not resizable inplace -> assert(0) and return false (read_buffer=%p, read_buffer_size=%zu)\n",
              connection->read_buffer, connection->read_buffer_size);
      fclose(logf);
    }
    mhd_assert (0);
    return false;
  }
  if (NULL != logf)
  {
    fprintf(logf, "Resizable in place or no existing read_buffer -> proceed to reallocate (read_buffer=%p, read_buffer_size=%zu)\n",
            connection->read_buffer, connection->read_buffer_size);
    fflush(logf);
  }
  /* we can actually grow the buffer, do it! */
  rb = MHD_pool_reallocate (connection->pool,
                            connection->read_buffer,
                            connection->read_buffer_size,
                            new_size);
  if (NULL == rb)
  {
    /* This should NOT be possible: we just computed 'new_size' so that
       it should fit. If it happens, somehow our read buffer is not in
       the right position in the pool, say because someone called
       MHD_pool_allocate() without 'from_end' set to 'true'? Anyway,
       should be investigated! (Ideally provide all data from
       *pool and connection->read_buffer and new_size for debugging). */
    if (NULL != logf)
    {
      fprintf(logf, "MHD_pool_reallocate returned NULL -> assert(0) and return false (new_size=%zu, read_buffer=%p, read_buffer_size=%zu)\n",
              new_size, connection->read_buffer, connection->read_buffer_size);
      fclose(logf);
    }
    mhd_assert (0);
    return false;
  }
  if (NULL != logf)
  {
    fprintf(logf, "MHD_pool_reallocate returned rb=%p\n", rb);
    fflush(logf);
  }
  if (NULL != logf)
  {
    int equal = (connection->read_buffer == rb);
    fprintf(logf, "Before assignment: connection->read_buffer=%p, rb=%p, equal=%d\n",
            connection->read_buffer, rb, equal);
    fflush(logf);
  }
  mhd_assert (connection->read_buffer == rb);
  connection->read_buffer = rb;
  if (NULL != logf)
  {
    fprintf(logf, "After assignment: connection->read_buffer=%p, read_buffer_size will be=%zu\n",
            connection->read_buffer, new_size);
    fprintf(logf, "EXIT try_grow_read_buffer -> returning true\n");
    fclose(logf);
  }
  mhd_assert (NULL != connection->read_buffer);
  connection->read_buffer_size = new_size;
  return true;
}
