static int
try_match_header (const char *prefix,
                  size_t prefix_len,
                  char *line,
                  char **suffix)
{
  FILE *log = fopen("/tmp/logs/4272.txt", "a");
  if (NULL != log)
  {
    fprintf(log, "try_match_header ENTER: prefix=%p prefix_len=%zu line=%p line_first_char=0x%02x suffix_ptr=%p\n",
            (const void *)prefix, prefix_len, (void *)line,
            (unsigned int)((unsigned char) (line != NULL ? *line : 0)), (void *)suffix);
    fflush(log);
  }

  if (NULL != *suffix)
  {
    if (NULL != log)
    {
      fprintf(log, "Early exit: *suffix != NULL (suffix=%p) -> returning MHD_NO\n", (void *)*suffix);
      fflush(log);
      fclose(log);
    }
    return MHD_NO;
  }

  {
    int iter = 0;
    while (0 != *line)
    {
      if (NULL != log)
      {
        fprintf(log, "ITER %d: line=%p char=0x%02x str_segment='%.32s'\n",
                iter, (void *)line, (unsigned int)((unsigned char)*line), line);
        fflush(log);
      }

      /* preserve original call/logic */
      if (MHD_str_equal_caseless_n_ (prefix,
                                     line,
                                     prefix_len))
      {
        if (NULL != log)
        {
          fprintf(log, "Match found at line=%p. suffix source=&line[%zu]=%p ('%.32s')\n",
                  (void *)line, prefix_len, (void *)&line[prefix_len], &line[prefix_len]);
          fflush(log);
        }

        *suffix = strdup (&line[prefix_len]);

        if (NULL != log)
        {
          fprintf(log, "strdup returned %p assigned to *suffix. Returning MHD_YES\n", (void *)*suffix);
          fflush(log);
          fclose(log);
        }
        return MHD_YES;
      }

      if (NULL != log)
      {
        fprintf(log, "No match at line=%p; incrementing pointer (violation scenario: modifying parameter 'line')\n",
                (void *)line);
        fflush(log);
      }

      ++line; /* This modifies the pointer parameter as in the original code */

      if (NULL != log)
      {
        fprintf(log, "After ++line -> new line=%p char=0x%02x\n",
                (void *)line, (unsigned int)((unsigned char)*line));
        fflush(log);
      }

      ++iter;
    }
  }

  if (NULL != log)
  {
    fprintf(log, "End of input reached, no match -> returning MHD_NO\n");
    fflush(log);
    fclose(log);
  }
  return MHD_NO;
}
