static int
try_match_header (const char *prefix,
                  size_t prefix_len,
                  char *line,
                  char **suffix)
{
  /* Logging probe to capture control flow and state for Rule 17.8 reproduction. */
  FILE *log = fopen("/tmp/logs/4272.txt", "a");
  if (log)
  {
    fprintf(log, "try_match_header ENTRY: prefix=%p prefix_len=%zu line=%p suffix_ptr=%p\n",
            (const void *)prefix, prefix_len, (void *)line, (void *)suffix);
    if (prefix != NULL)
    {
      size_t _pprint_max = prefix_len < 64u ? prefix_len : 64u;
      fprintf(log, "prefix (up to %zu bytes): ", _pprint_max);
      for (size_t _i = 0u; _i < _pprint_max; ++_i)
        fprintf(log, "%02x ", (unsigned char)prefix[_i]);
      fprintf(log, "\n");
    }
    if (suffix != NULL)
      fprintf(log, "initial *suffix = %p\n", (void *)(*suffix));
    fflush(log);
  }

  if (NULL != *suffix)
  {
    if (log)
    {
      fprintf(log, "exit: *suffix already set -> returning MHD_NO\n");
      fclose(log);
    }
    return MHD_NO;
  }

  /* Iterate over the line. The original code increments the pointer parameter 'line',
     which is the subject of the MISRA Rule 17.8 report. We preserve that behavior
     and log each increment to provide evidence. */
  size_t _iter = 0u;
  while (0 != *line)
  {
    if (log)
    {
      unsigned char _c = (unsigned char)*line;
      fprintf(log, "iter %zu: line=%p char=0x%02x (%c)\n",
              _iter, (void *)line, _c, (_c >= 32u && _c <= 126u) ? (char)_c : '.');
      fflush(log);
    }

    if (MHD_str_equal_caseless_n_ (prefix,
                                   line,
                                   prefix_len))
    {
      if (log)
        fprintf(log, "match at iter %zu: allocating suffix from &line[%zu] (line=%p)\n",
                _iter, prefix_len, (void *)line);

      *suffix = strdup (&line[prefix_len]);

      if (log)
      {
        fprintf(log, "after strdup: *suffix=%p -> returning MHD_YES\n", (void *)(*suffix));
        fclose(log);
      }
      return MHD_YES;
    }

    if (log)
      fprintf(log, "about to increment line pointer: before=%p\n", (void *)line);

    /* This is the operation reported as the Rule 17.8 violation. */
    ++line;

    if (log)
    {
      fprintf(log, "after increment: line=%p\n", (void *)line);
      fflush(log);
    }

    ++_iter;
  }

  if (log)
  {
    fprintf(log, "end of string reached -> returning MHD_NO\n");
    fclose(log);
  }

  return MHD_NO;
}
