static int
try_match_header (const char *prefix,
                  size_t prefix_len,
                  char *line,
                  char **suffix)
{
  /* Entry log: record pointers and a short preview of the line */
  {
    FILE *log = fopen("/tmp/logs/4275.txt", "a");
    if (log)
    {
      fprintf(log, "try_match_header entered: prefix=%p prefix_len=%zu line=%p suffix=%p\n",
              (void *)prefix, prefix_len, (void *)line, (void *)suffix);
      if (line)
      {
        fprintf(log, "line (preview, up to 64 bytes): '");
        size_t li;
        for (li = 0U; li < 64U && line[li] != '\0'; ++li)
        {
          unsigned char c = (unsigned char)line[li];
          if (c >= 32U && c <= 126U)
            fputc(c, log);
          else
            fprintf(log, "\\x%02x", c);
        }
        fprintf(log, "'\n");
      }
      else
      {
        fprintf(log, "line = (null)\n");
      }
      if (suffix)
        fprintf(log, "initial *suffix = %p\n", (void *)(*suffix));
      else
        fprintf(log, "suffix pointer = (null)\n");
      fclose(log);
    }
  }

  /* Preserve original behavior: check incoming *suffix (this dereferences suffix)
     and return MHD_NO if non-NULL. Log this control decision. */
  if (NULL != *suffix)
  {
    FILE *log = fopen("/tmp/logs/4275.txt", "a");
    if (log)
    {
      fprintf(log, "early return: *suffix != NULL (%p) -> MHD_NO\n", (void *)(*suffix));
      fclose(log);
    }
    return MHD_NO;
  }

  /* Iterate through line as in the original implementation. Log each comparison and
     the moment a match is found (or not). */
  size_t pos = 0U;
  while (0 != *line)
  {
    /* Log current position and character */
    {
      FILE *log = fopen("/tmp/logs/4275.txt", "a");
      if (log)
      {
        unsigned char c = (unsigned char)*line;
        if (c >= 32U && c <= 126U)
          fprintf(log, "loop pos=%zu line+pos=%p char='%c' (0x%02x)\n", pos, (void *)line, (char)c, c);
        else
          fprintf(log, "loop pos=%zu line+pos=%p char=0x%02x\n", pos, (void *)line, c);
        fclose(log);
      }
    }

    /* Perform original comparison and log the result */
    if (MHD_str_equal_caseless_n_ (prefix,
                                   line,
                                   prefix_len))
    {
      FILE *log = fopen("/tmp/logs/4275.txt", "a");
      if (log)
      {
        fprintf(log, "comparison at pos=%zu: match -> duplicating suffix from &line[%zu]\n", pos, prefix_len);
        fclose(log);
      }

      *suffix = strdup (&line[prefix_len]);

      /* Log the result of strdup (address or NULL) */
      {
        FILE *log = fopen("/tmp/logs/4275.txt", "a");
        if (log)
        {
          fprintf(log, "assigned *suffix = %p\n", (void *)(*suffix));
          fclose(log);
        }
      }

      return MHD_YES;
    }

    ++line;
    ++pos;
  }

  /* No match found: log and return MHD_NO */
  {
    FILE *log = fopen("/tmp/logs/4275.txt", "a");
    if (log)
    {
      fprintf(log, "no match found -> MHD_NO\n");
      fclose(log);
    }
  }
  return MHD_NO;
}
