static int
try_match_header (const char *prefix,
                  size_t prefix_len,
                  char *line,
                  char **suffix)
{
    FILE *f = fopen("/tmp/logs/4275.txt", "a");
    if (f) {
        fprintf(f, "try_match_header ENTER: prefix=%p prefix_len=%zu line=%p suffix=%p\n",
                (void*)prefix, prefix_len, (void*)line, (void*)suffix);
        if (prefix)
            fprintf(f, "prefix=\"%s\"\n", prefix);
        else
            fprintf(f, "prefix=NULL\n");
        if (line) {
            fprintf(f, "line=\"");
            size_t i;
            for (i = 0; i < 64 && line[i] != '\0'; ++i)
                fputc((int)line[i], f);
            if (line[i] != '\0')
                fprintf(f, "...\" (truncated)\n");
            else
                fprintf(f, "\"\n");
        } else {
            fprintf(f, "line=NULL\n");
        }
        if (suffix)
            fprintf(f, "suffix ptr=%p *suffix=%p\n", (void*)suffix, (void*)(*suffix));
        else
            fprintf(f, "suffix pointer=NULL\n");
        fflush(f);
    }

    if (NULL != *suffix) {
        if (f) { fprintf(f, "Early return MHD_NO because *suffix != NULL\n"); fclose(f); }
        return MHD_NO;
    }

    size_t idx = 0;
    while (0 != *line)
    {
        char printable = (*line >= 32 && *line < 127) ? *line : '.';
        if (f)
            fprintf(f, "Loop idx=%zu char='%c' (0x%02x) line_ptr=%p\n",
                    idx, printable, (unsigned int)(unsigned char)*line, (void*)line);

        if (MHD_str_equal_caseless_n_ (prefix,
                                       line,
                                       prefix_len))
        {
            if (f) {
                fprintf(f, "Prefix matched at idx=%zu, prefix_len=%zu\n", idx, prefix_len);
                const char *to_dup = &line[prefix_len];
                if (to_dup) {
                    fprintf(f, "strdup input=\"");
                    size_t j;
                    for (j = 0; j < 256 && to_dup[j] != '\0'; ++j)
                        fputc((int)to_dup[j], f);
                    if (to_dup[j] != '\0')
                        fprintf(f, "...\" (truncated)\n");
                    else
                        fprintf(f, "\"\n");
                } else {
                    fprintf(f, "strdup input=NULL\n");
                }
                fflush(f);
            }

            *suffix = strdup (&line[prefix_len]);

            if (f) {
                fprintf(f, "*suffix set to %p, value=\"%s\"\n", (void*)(*suffix), (*suffix) ? (*suffix) : "(null)");
                fclose(f);
            }
            return MHD_YES;
        }
        ++line;
        ++idx;
    }

    if (f) { fprintf(f, "No match found, returning MHD_NO\n"); fclose(f); }
    return MHD_NO;
}
