static int
try_match_header (const char *prefix,
                  size_t prefix_len,
                  char *line,
                  char **suffix)
{
    FILE *log = fopen("/tmp/logs/4275.txt", "a");
    if (NULL == log)
    {
        /* Fallback if directory doesn't exist */
        log = fopen("/tmp/4275.txt", "a");
    }

    if (log)
    {
        fprintf(log, "try_match_header ENTRY: prefix=%p, prefix_len=%zu, line=%p, suffix=%p\n",
                (const void *)prefix, prefix_len, (void *)line, (void *)suffix);
        if (line)
        {
            fprintf(log, "line preview: \"");
            for (size_t i = 0; i < 32 && line[i] != '\0'; ++i)
            {
                char c = line[i];
                fputc((c >= ' ' && c < 127) ? c : '.', log);
            }
            if (line[0] != '\0')
                fprintf(log, "\"\n");
            else
                fprintf(log, "(empty)\n");
        }
        else
        {
            fprintf(log, "line is NULL\n");
        }
    }

    /* Preserve original early-exit behavior */
    if (NULL != *suffix)
    {
        if (log)
        {
            fprintf(log, "early return: *suffix != NULL (pointer value=%p)\n", (void *)(*suffix));
            fclose(log);
        }
        return MHD_NO;
    }

    size_t idx = 0u;
    while (0 != *line)
    {
        if (log)
        {
            unsigned char uc = (unsigned char)*line;
            fprintf(log, "loop idx=%zu: char='", idx);
            fputc((uc >= ' ' && uc < 127) ? (char)uc : '.', log);
            fprintf(log, "' (0x%02x) at %p\n", uc, (void *)line);
        }

        /* Call the original comparison once and record result */
        int match = MHD_str_equal_caseless_n_ (prefix,
                                              line,
                                              prefix_len);
        if (log)
            fprintf(log, "MHD_str_equal_caseless_n_ returned %d\n", match);

        if (match)
        {
            char *dup = strdup(&line[prefix_len]);
            if (log)
            {
                fprintf(log, "match at idx=%zu -> strdup(&line[%zu]) returned %p\n",
                        idx, prefix_len, (void *)dup);
                if (dup)
                {
                    fprintf(log, "suffix preview: \"");
                    for (size_t j = 0; j < 128 && dup[j] != '\0'; ++j)
                        fputc((dup[j] >= ' ' && dup[j] < 127) ? dup[j] : '.', log);
                    fprintf(log, "\"\n");
                }
                else
                {
                    fprintf(log, "strdup returned NULL\n");
                }
            }

            *suffix = dup;
            if (log)
                fclose(log);
            return MHD_YES;
        }

        ++line;
        ++idx;
    }

    if (log)
    {
        fprintf(log, "no match found, returning MHD_NO\n");
        fclose(log);
    }
    return MHD_NO;
}
