static int
try_match_header (const char *prefix,
                  size_t prefix_len,
                  char *line,
                  char **suffix)
{
  /* Probe/logging: capture entry and initial state. Keep logic identical. */
  {
    FILE *log = fopen("/tmp/logs/4275.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "try_match_header: entered\n");
      fprintf(log, "  prefix=%p, prefix_len=%zu, line=%p, suffix_ptr=%p\n",
              (void *)prefix, prefix_len, (void *)line, (void *)suffix);
      if (NULL != suffix)
      {
        fprintf(log, "  *suffix (initial)=%p\n", (void *)*suffix);
      }
      fclose(log);
    }
  }

  if (NULL != *suffix)
  {
    /* Log early-return due to non-NULL *suffix */
    FILE *log = fopen("/tmp/logs/4275.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "try_match_header: early return MHD_NO because *suffix != NULL (value=%p)\n", (void *)*suffix);
      fclose(log);
    }
    return MHD_NO;
  }

  size_t pos = 0u;
  while (0 != *line)
  {
    /* Log current character and position (safe, does not modify line) */
    {
      FILE *log = fopen("/tmp/logs/4275.txt", "a");
      if (NULL != log)
      {
        unsigned char uc = (unsigned char)*line;
        fprintf(log, "try_match_header: loop pos=%zu, line[%zu]=" , pos, pos);
        /* Print a printable representation: if printable, print char, else show hex */
        if (uc >= 0x20u && uc < 0x7Fu)
        {
          fprintf(log, "'%c' (0x%02X)\n", (char)uc, uc);
        }
        else
        {
          fprintf(log, "(non-printable) 0x%02X\n", uc);
        }
        fclose(log);
      }
    }

    if (MHD_str_equal_caseless_n_ (prefix,
                                   line,
                                   prefix_len))
    {
      /* Log the match and the suffix being duplicated. Keep behavior identical. */
      FILE *log = fopen("/tmp/logs/4275.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "try_match_header: prefix matched at pos=%zu\n", pos);
        if (line[prefix_len] != '\0')
        {
          /* Log a preview of the suffix string to aid reproduction. */
          fprintf(log, "  suffix text preview=\"%s\"\n", &line[prefix_len]);
        }
        else
        {
          fprintf(log, "  suffix text preview=(empty)\n");
        }
        fclose(log);
      }

      *suffix = strdup (&line[prefix_len]);

      log = fopen("/tmp/logs/4275.txt", "a");
      if (NULL != log)
      {
        fprintf(log, "try_match_header: returning MHD_YES, *suffix=%p\n", (void *)*suffix);
        fclose(log);
      }
      return MHD_YES;
    }

    ++line;
    ++pos;
  }

  {
    FILE *log = fopen("/tmp/logs/4275.txt", "a");
    if (NULL != log)
    {
      fprintf(log, "try_match_header: end of line reached, no match, returning MHD_NO\n");
      fclose(log);
    }
  }

  return MHD_NO;
}
