static enum MHD_Result
try_ready_chunked_body (struct MHD_Connection *connection,
                        bool *p_finished)
{
  ssize_t ret;
  struct MHD_Response *response;
  static const size_t max_chunk = 0xFFFFFF;
  char chunk_hdr[6];            /* 6: max strlen of "FFFFFF" */
  /* "FFFFFF" + "\r\n" */
  static const size_t max_chunk_hdr_len = sizeof(chunk_hdr) + 2;
  /* "FFFFFF" + "\r\n" + "\r\n" (chunk termination) */
  static const size_t max_chunk_overhead = sizeof(chunk_hdr) + 2 + 2;
  size_t chunk_hdr_len;
  uint64_t left_to_send;
  size_t size_to_fill;

  /* Open log file for tracing control flow and state */
  FILE *mhd_log = fopen("/tmp/logs/1608.txt", "a");
  if (mhd_log)
  {
    fprintf(mhd_log, "ENTER try_ready_chunked_body: connection=%p\n", (void *)connection);
    fflush(mhd_log);
  }

  response = connection->rp.response;
  if (mhd_log)
  {
    fprintf(mhd_log,
            "response=%p response->crc=%p response->data=%p response->total_size=%llu response->data_start=%llu response->data_size=%zu\n",
            (void *)response,
            (void *)response->crc,
            (void *)response->data,
            (unsigned long long) response->total_size,
            (unsigned long long) response->data_start,
            (size_t) response->data_size);
    fprintf(mhd_log, "connection->write_buffer=%p write_buffer_size=%zu write_buffer_append_offset=%zu rp.rsp_write_position=%zu pool=%p\n",
            (void *)connection->write_buffer,
            connection->write_buffer_size,
            connection->write_buffer_append_offset,
            connection->rp.rsp_write_position,
            (void *)connection->pool);
    fflush(mhd_log);
  }

  mhd_assert (NULL != response->crc || NULL != response->data);

  mhd_assert (0 == connection->write_buffer_append_offset);

  /* The buffer must be reasonably large enough */
  if (128 > connection->write_buffer_size)
  {
    size_t size;

    size = connection->write_buffer_size + MHD_pool_get_free (connection->pool);
    if (mhd_log)
    {
      fprintf(mhd_log, "Calculated size after pool free: %zu (write_buffer_size=%zu + pool_free=%zu)\n",
              size, connection->write_buffer_size, (size_t) MHD_pool_get_free(connection->pool));
      fflush(mhd_log);
    }
    if (128 > size)
    {
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
      MHD_mutex_unlock_chk_ (&response->mutex);
#endif
      /* not enough memory */
      if (mhd_log)
      {
        fprintf(mhd_log, "Not enough memory: closing connection.\n");
        fclose(mhd_log);
      }
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("Closing connection (out of memory)."));
      return MHD_NO;
    }
    /* Limit the buffer size to the largest usable size for chunks */
    if ( (max_chunk + max_chunk_overhead) < size)
      size = max_chunk + max_chunk_overhead;

    /* Probe: call MHD_pool_is_resizable_inplace and log result so that the
       condition inside the assertion (which may be compiled out) is
       recorded for diagnostics. This preserves original behavior except
       that we evaluate and log the predicate unconditionally. */
    int pool_is_resizable = 0;
    if (connection->pool != NULL)
      pool_is_resizable = MHD_pool_is_resizable_inplace (connection->pool,
                                                         connection->write_buffer,
                                                         connection->write_buffer_size);
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_pool_is_resizable_inplace -> %d (pool=%p, write_buffer=%p, size=%zu)\n",
              pool_is_resizable, (void *)connection->pool, (void *)connection->write_buffer,
              connection->write_buffer_size);
      fflush(mhd_log);
    }

    /* Preserve original assertion semantics but reuse computed value so
       that the function is not called twice when assertions are enabled. */
    mhd_assert ((NULL == connection->write_buffer) || pool_is_resizable);
    connection->write_buffer =
      MHD_pool_reallocate (connection->pool,
                           connection->write_buffer,
                           connection->write_buffer_size,
                           size);
    mhd_assert (NULL != connection->write_buffer);
    connection->write_buffer_size = size;
    if (mhd_log)
    {
      fprintf(mhd_log, "After reallocate: write_buffer=%p write_buffer_size=%zu\n",
              (void *)connection->write_buffer, connection->write_buffer_size);
      fflush(mhd_log);
    }
  }
  mhd_assert (max_chunk_overhead < connection->write_buffer_size);

  if (MHD_SIZE_UNKNOWN == response->total_size)
    left_to_send = MHD_SIZE_UNKNOWN;
  else
    left_to_send = response->total_size
                   - connection->rp.rsp_write_position;

  if (mhd_log)
  {
    fprintf(mhd_log, "left_to_send=%llu size_to_fill (pre) = write_buffer_size - max_chunk_overhead (%zu - %zu)\n",
            (unsigned long long) left_to_send,
            connection->write_buffer_size, (size_t) max_chunk_overhead);
    fflush(mhd_log);
  }

  size_to_fill = connection->write_buffer_size - max_chunk_overhead;
  /* Limit size for the callback to the max usable size */
  if (max_chunk < size_to_fill)
    size_to_fill = max_chunk;
  if (left_to_send < size_to_fill)
    size_to_fill = (size_t) left_to_send;

  if (0 == left_to_send)
    /* nothing to send, don't bother calling crc */
    ret = MHD_CONTENT_READER_END_OF_STREAM;
  else if ( (response->data_start <=
             connection->rp.rsp_write_position) &&
            (response->data_start + response->data_size >
             connection->rp.rsp_write_position) )
  {
    /* difference between rsp_write_position and data_start is less
       than data_size which is size_t type, no need to check for overflow */
    const size_t data_write_offset
      = (size_t) (connection->rp.rsp_write_position
                  - response->data_start);
    /* buffer already ready, use what is there for the chunk */
    mhd_assert (SSIZE_MAX >= (response->data_size - data_write_offset));
    mhd_assert (response->data_size >= data_write_offset);
    ret = (ssize_t) (response->data_size - data_write_offset);
    if ( ((size_t) ret) > size_to_fill)
      ret = (ssize_t) size_to_fill;
    memcpy (&connection->write_buffer[max_chunk_hdr_len],
            &response->data[data_write_offset],
            (size_t) ret);
    if (mhd_log)
    {
      fprintf(mhd_log, "Used in-memory data: data_write_offset=%zu ret=%zd size_to_fill=%zu\n",
              data_write_offset, ret, size_to_fill);
      fflush(mhd_log);
    }
  }
  else
  {
    if (NULL == response->crc)
    { /* There is no way to reach this code */
#if defined(MHD_USE_THREADS)
      MHD_mutex_unlock_chk_ (&response->mutex);
#endif
      if (mhd_log)
      {
        fprintf(mhd_log, "ERROR: response->crc is NULL but branch taken. Closing connection.\n");
        fclose(mhd_log);
      }
      CONNECTION_CLOSE_ERROR (connection,
                              _ ("No callback for the chunked data."));
      return MHD_NO;
    }
    if (mhd_log)
    {
      fprintf(mhd_log, "Calling response->crc(cls=%p, pos=%zu, buf=%p, size_to_fill=%zu)\n",
              response->crc_cls,
              connection->rp.rsp_write_position,
              (void *) &connection->write_buffer[max_chunk_hdr_len],
              size_to_fill);
      fflush(mhd_log);
    }
    ret = response->crc (response->crc_cls,
                         connection->rp.rsp_write_position,
                         &connection->write_buffer[max_chunk_hdr_len],
                         size_to_fill);
    if (mhd_log)
    {
      fprintf(mhd_log, "response->crc returned %zd\n", ret);
      fflush(mhd_log);
    }
  }
  if (MHD_CONTENT_READER_END_WITH_ERROR == ret)
  {
    /* error, close socket! */
    /* TODO: remove update of the response size */
    response->total_size = connection->rp.rsp_write_position;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_CONTENT_READER_END_WITH_ERROR: closing connection (application error).\n");
      fclose(mhd_log);
    }
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection (application error " \
                               "generating response)."));
    return MHD_NO;
  }
  if (MHD_CONTENT_READER_END_OF_STREAM == ret)
  {
    *p_finished = true;
    /* TODO: remove update of the response size */
    response->total_size = connection->rp.rsp_write_position;
    if (mhd_log)
    {
      fprintf(mhd_log, "MHD_CONTENT_READER_END_OF_STREAM: finished. rsp_write_position=%zu\n",
              connection->rp.rsp_write_position);
      fclose(mhd_log);
    }
    return MHD_YES;
  }
  if (0 == ret)
  {
    connection->state = MHD_CONNECTION_CHUNKED_BODY_UNREADY;
#if defined(MHD_USE_POSIX_THREADS) || defined(MHD_USE_W32_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    if (mhd_log)
    {
      fprintf(mhd_log, "crc returned 0: set state UNREADY and closing.\n");
      fclose(mhd_log);
    }
    return MHD_NO;
  }
  if (size_to_fill < (size_t) ret)
  {
#if defined(MHD_USE_THREADS)
    MHD_mutex_unlock_chk_ (&response->mutex);
#endif
    if (mhd_log)
    {
      fprintf(mhd_log, "Application returned more data than requested: ret=%zd size_to_fill=%zu\n",
              ret, size_to_fill);
      fclose(mhd_log);
    }
    CONNECTION_CLOSE_ERROR (connection,
                            _ ("Closing connection (application returned " \
                               "more data than requested)."));
    return MHD_NO;
  }
  chunk_hdr_len = MHD_uint32_to_strx ((uint32_t) ret, chunk_hdr,
                                      sizeof(chunk_hdr));
  mhd_assert (chunk_hdr_len != 0);
  mhd_assert (chunk_hdr_len < sizeof(chunk_hdr));
  *p_finished = false;
  connection->write_buffer_send_offset =
    (max_chunk_hdr_len - (chunk_hdr_len + 2));
  memcpy (connection->write_buffer + connection->write_buffer_send_offset,
          chunk_hdr,
          chunk_hdr_len);
  connection->write_buffer[max_chunk_hdr_len - 2] = '\r';
  connection->write_buffer[max_chunk_hdr_len - 1] = '\n';
  connection->write_buffer[max_chunk_hdr_len + (size_t) ret] = '\r';
  connection->write_buffer[max_chunk_hdr_len + (size_t) ret + 1] = '\n';
  connection->rp.rsp_write_position += (size_t) ret;
  connection->write_buffer_append_offset = max_chunk_hdr_len + (size_t) ret + 2;

  if (mhd_log)
  {
    fprintf(mhd_log, "Chunk prepared: ret=%zd chunk_hdr_len=%zu write_buffer_send_offset=%zu append_offset=%zu rsp_write_position=%zu\n",
            ret, chunk_hdr_len, connection->write_buffer_send_offset,
            connection->write_buffer_append_offset, connection->rp.rsp_write_position);
    fclose(mhd_log);
  }
  return MHD_YES;
}
