static size_t
unescape_wrapper (void *cls,
                  struct MHD_Connection *connection,
                  char *val)
{
  bool broken;
  size_t res;
  (void) cls; /* Mute compiler warning. */

  /* Logging probe: record entry, pointer state and numeric value of client_discipline. */
  {
    FILE *log = fopen("/tmp/logs/2736.txt", "a");
    if (log)
    {
      /* Note: deliberately dereference connection->daemon->client_discipline exactly as the
         original code does so that behavior (including crashes) is preserved. */
      fprintf(log, "unescape_wrapper ENTER: connection=%p daemon=%p\n",
              (void *) connection,
              (void *) connection->daemon);

      /* Print both signed and unsigned interpretations so callers can observe whether
         client_discipline is unsigned (which would make the comparison tautological). */
      fprintf(log, "client_discipline (signed) = %lld\n",
              (long long) connection->daemon->client_discipline);
      fprintf(log, "client_discipline (unsigned) = %llu\n",
              (unsigned long long) connection->daemon->client_discipline);

      /* Log the boolean value of the condition used in the branch. */
      fprintf(log, "condition (0 <= client_discipline) = %d\n",
              (int) (0 <= connection->daemon->client_discipline));

      fflush(log);
      fclose(log);
    }
  }

  /* Preserve original logic: if the condition holds, take the strict decode path. */
  if (0 <= connection->daemon->client_discipline)
  {
    FILE *log = fopen("/tmp/logs/2736.txt", "a");
    if (log)
    {
      fprintf(log, "branch: strict decode taken\n");
      fflush(log);
      fclose(log);
    }

    return MHD_str_pct_decode_in_place_strict_ (val);
  }

  res = MHD_str_pct_decode_in_place_lenient_ (val, &broken);

#ifdef HAVE_MESSAGES
  if (broken)
  {
    MHD_DLOG (connection->daemon,
              _ ("The URL encoding is broken.\n"));
  }
#endif /* HAVE_MESSAGES */

  {
    FILE *log = fopen("/tmp/logs/2736.txt", "a");
    if (log)
    {
      fprintf(log, "branch: lenient decode taken, res=%zu broken=%d\n",
              res, (int) broken);
      fflush(log);
      fclose(log);
    }
  }

  return res;
}
