static size_t
unescape_wrapper (void *cls,
                  struct MHD_Connection *connection,
                  char *val)
{
  bool broken;
  size_t res;
  FILE *log = fopen("/tmp/logs/2738.txt", "a");

  if (log)
  {
    /* Log entry and raw pointers/state so test harness can observe control flow */
    fprintf(log, "ENTER unescape_wrapper: cls=%p connection=%p val=%p\n",
            cls, (void *) connection, (void *) val);
    /* Mirror the original access to daemon->client_discipline so behavior/crash characteristics remain */
    fprintf(log, "daemon=%p client_discipline=%d\n",
            (void *) connection->daemon,
            connection->daemon->client_discipline);
    fprintf(log, "About to execute '(void) cls;' statement\n");
    fprintf(log, "cls before=%p\n", cls);
    fflush(log);
  }

  /* Original statement that was reported as dead code. Keep it but log its execution. */
  (void) cls;

  if (log)
  {
    fprintf(log, "Executed '(void) cls;'\n");
    fprintf(log, "cls after=%p  // no change expected\n", cls);
    fflush(log);
  }

  /* Preserve original branching and return behavior. Log results for evidence. */
  if (0 <= connection->daemon->client_discipline)
  {
    size_t tmp = MHD_str_pct_decode_in_place_strict_ (val);
    if (log)
    {
      fprintf(log, "Taking strict decode path, returned=%zu\n", tmp);
      fclose(log);
    }
    return tmp;
  }

  res = MHD_str_pct_decode_in_place_lenient_ (val, &broken);
  if (log)
  {
    fprintf(log, "Taking lenient decode path, returned=%zu broken=%d\n", res, broken ? 1 : 0);
    fflush(log);
    fclose(log);
  }

#ifdef HAVE_MESSAGES
  if (broken)
  {
    MHD_DLOG (connection->daemon,
              _ ("The URL encoding is broken.\n"));
  }
#endif /* HAVE_MESSAGES */
  return res;
}
