static size_t
unescape_wrapper (void *cls,
                  struct MHD_Connection *connection,
                  char *val)
{
  bool broken;
  size_t res;
  /* Open log file for appending; record entry and relevant state to aid debugging. */
  {
    FILE *log = fopen("/tmp/logs/2735.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "unescape_wrapper: entered\n");
      fprintf(log, "  cls pointer: %p\n", cls);
      fprintf(log, "  cls_is_null: %d\n", (cls == NULL) ? 1 : 0);
      fprintf(log, "  val pointer: %p\n", (void *) val);
      if (val != NULL)
      {
        /* Print a truncated view of the string to avoid overly large logs. */
        size_t i;
        size_t max_print = 256U;
        fprintf(log, "  val (up to %zu bytes): \"", max_print);
        for (i = 0U; i < max_print && val[i] != '\0'; ++i)
        {
          int c = (unsigned char) val[i];
          /* Escape non-printable characters as hex */
          if (c >= 32 && c <= 126)
            fputc((char) c, log);
          else
            fprintf(log, "\\x%02X", c);
        }
        if (val[0] == '\0')
          fprintf(log, "(empty)");
        else if (val[max_print] != '\0')
          fprintf(log, "... (truncated)");
        fprintf(log, "\"\n");
      }
      if (connection == NULL)
      {
        fprintf(log, "  connection: NULL\n");
      }
      else
      {
        fprintf(log, "  connection pointer: %p\n", (void *) connection);
        if (connection->daemon == NULL)
        {
          fprintf(log, "  connection->daemon: NULL\n");
        }
        else
        {
          fprintf(log, "  connection->daemon pointer: %p\n", (void *) connection->daemon);
          fprintf(log, "  connection->daemon->client_discipline: %d\n",
                  connection->daemon->client_discipline);
        }
      }
      fclose(log);
    }
  }

  (void) cls; /* Mute compiler warning. */

  /* TODO: add individual parameter */
  if (0 <= connection->daemon->client_discipline)
    return MHD_str_pct_decode_in_place_strict_ (val);

  res = MHD_str_pct_decode_in_place_lenient_ (val, &broken);
#ifdef HAVE_MESSAGES
  if (broken)
  {
    MHD_DLOG (connection->daemon,
              _ ("The URL encoding is broken.\n"));
  }
#endif /* HAVE_MESSAGES */

  /* Also log the result and whether the lenient decode marked the string as broken. */
  {
    FILE *log = fopen("/tmp/logs/2735.txt", "a");
    if (log != NULL)
    {
      fprintf(log, "unescape_wrapper: returning from lenient path\n");
      fprintf(log, "  res: %zu\n", res);
      fprintf(log, "  broken flag: %d\n", broken ? 1 : 0);
      fclose(log);
    }
  }

  return res;
}
