int lfs_dir_close(lfs_t *lfs, lfs_dir_t *dir) {
    /* Open a persistent log for this test run. Append so multiple calls accumulate. */
    FILE *fp = fopen("/tmp/logs/21.txt", "a");
    if (fp) {
        fprintf(fp, "ENTER lfs_dir_close: lfs=%p dir=%p\n", (void*)lfs, (void*)dir);
        fflush(fp);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (fp) {
        fprintf(fp, "AFTER LFS_LOCK -> %d\n", err);
        fflush(fp);
    }

    if (err) {
        if (fp) {
            fprintf(fp, "EARLY RETURN from lfs_dir_close due to lock error: %d\n", err);
            fclose(fp);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_close(%p, %p)", (void*)lfs, (void*)dir);
    if (fp) {
        fprintf(fp, "CALL lfs_dir_close_: lfs=%p dir=%p\n", (void*)lfs, (void*)dir);
        fflush(fp);
    }

    err = lfs_dir_close_(lfs, dir);

    if (fp) {
        fprintf(fp, "RETURN from lfs_dir_close_: %d\n", err);
        fflush(fp);
    }

    LFS_TRACE("lfs_dir_close -> %d", err);

    if (fp) {
        fprintf(fp, "BEFORE UNLOCK lfs_dir_close final err=%d lfs=%p dir=%p\n", err, (void*)lfs, (void*)dir);
        fclose(fp);
    }

    LFS_UNLOCK(lfs->cfg);
    return err;
}
