int lfs_dir_close(lfs_t *lfs, lfs_dir_t *dir) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open a persistent append log to capture control flow and state. */
    FILE *logf = fopen("/tmp/logs/29.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_dir_close: lfs=%p dir=%p initial_err=%d\n", (void*)lfs, (void*)dir, err);
        fflush(logf);
    } else {
        /* Fallback trace if file can't be opened. Keep original tracing facility. */
        LFS_TRACE("lfs_dir_close: failed to open /tmp/logs/29.txt");
    }

    if (err) {
        if (logf) {
            fprintf(logf, "lfs_dir_close: early return due to lock error err=%d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_close(%p, %p)", (void*)lfs, (void*)dir);

    if (logf) {
        fprintf(logf, "lfs_dir_close: acquired lock, calling lfs_dir_close_()\n");
        fflush(logf);
    }

    err = lfs_dir_close_(lfs, dir);

    if (logf) {
        fprintf(logf, "lfs_dir_close_: returned %d\n", err);
        fflush(logf);
    }

    LFS_TRACE("lfs_dir_close -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "lfs_dir_close: unlocked and returning %d\n", err);
        fclose(logf);
    }

    return err;
}
