int lfs_dir_close(lfs_t *lfs, lfs_dir_t *dir) {
    /* Open log file to record control flow and state for the Rule 2.2 investigation. */
    FILE *log = fopen("/tmp/logs/28.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_dir_close: lfs=%p, dir=%p\n", (void*)lfs, (void*)dir);
        if (lfs) {
            /* Print the cfg pointer value. If cfg evaluation has side-effects (volatile/I/O),
               those side-effects would have occurred when reading this value in typical contexts. */
            fprintf(log, "  before LFS_LOCK: lfs->cfg=%p\n", (const void*)lfs->cfg);
        } else {
            fprintf(log, "  before LFS_LOCK: lfs is NULL\n");
        }
    }

    int err = LFS_LOCK(lfs->cfg);

    if (log) {
        fprintf(log, "  after LFS_LOCK: err=%d\n", err);
    }

    if (err) {
        if (log) {
            fprintf(log, "  LFS_LOCK returned non-zero, returning %d (no unlock performed)\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_close(%p, %p)", (void*)lfs, (void*)dir);
    if (log) {
        fprintf(log, "  called LFS_TRACE and about to call lfs_dir_close_\n");
    }

    err = lfs_dir_close_(lfs, dir);

    LFS_TRACE("lfs_dir_close -> %d", err);
    if (log) {
        fprintf(log, "  lfs_dir_close_ returned %d\n", err);
    }

    LFS_UNLOCK(lfs->cfg);
    if (log) {
        fprintf(log, "  called LFS_UNLOCK(lfs->cfg)\n");
        fclose(log);
    }

    return err;
}
