int lfs_dir_close(lfs_t *lfs, lfs_dir_t *dir) {
#include <stdio.h>
    /* Preserve original locking behavior */
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file for append to record control flow and state. */
    FILE *logf = fopen("/tmp/logs/28.txt", "a");
    if (logf) {
        void *cfgval = (lfs != NULL) ? (void*)lfs->cfg : NULL;
        fprintf(logf, "lfs_dir_close: enter. lfs=%p dir=%p cfg=%p\n",
                (void*)lfs, (void*)dir, cfgval);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "lfs_dir_close: LFS_LOCK returned err=%d -> early return\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_close(%p, %p)", (void*)lfs, (void*)dir);

    err = lfs_dir_close_(lfs, dir);

    LFS_TRACE("lfs_dir_close -> %d", err);

    if (logf) {
        void *cfgval = (lfs != NULL) ? (void*)lfs->cfg : NULL;
        fprintf(logf, "lfs_dir_close: after lfs_dir_close_ -> err=%d cfg(before unlock)=%p\n",
                err, cfgval);
        fflush(logf);
    }

    /* Probe point: log just before and after the unlock. This captures whether
       the call has any observable side effects in practice. */
    if (logf) {
        fprintf(logf, "lfs_dir_close: calling LFS_UNLOCK(lfs->cfg) with cfg=%p\n",
                (void*)(lfs != NULL ? (void*)lfs->cfg : NULL));
        fflush(logf);
    }

    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "lfs_dir_close: returned from LFS_UNLOCK(lfs->cfg)\n");
        fprintf(logf, "lfs_dir_close: exit -> err=%d\n", err);
        fclose(logf);
    }

    return err;
}
