int lfs_dir_close(lfs_t *lfs, lfs_dir_t *dir) {
    /* Local includes to ensure availability of I/O functions when this
       function is substituted into the original source file. These are
       preprocessor directives and will be handled before compilation. */
    #include <stdio.h>
    #include <sys/stat.h>
    #include <errno.h>

    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Ensure log directory exists (best-effort). */
        (void)mkdir("/tmp/logs", 0777);
        FILE *f = fopen("/tmp/logs/28.txt", "a");
        if (f) {
            fprintf(f, "lfs_dir_close: LFS_LOCK returned %d; lfs=%p, cfg=%p, dir=%p\n",
                    err, (void*)lfs, (void*)lfs->cfg, (void*)dir);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_close(%p, %p)", (void*)lfs, (void*)dir);

    err = lfs_dir_close_(lfs, dir);

    LFS_TRACE("lfs_dir_close -> %d", err);

    /* Open log for capturing the evaluation and any potential side-effects
       of the expression passed to LFS_UNLOCK. This documents the value
       of lfs->cfg before and after the unlock call so other agents can
       reproduce the reported dead-code scenario. */
    (void)mkdir("/tmp/logs", 0777);
    FILE *f = fopen("/tmp/logs/28.txt", "a");
    if (f) {
        fprintf(f, "lfs_dir_close: before LFS_UNLOCK: lfs=%p, dir=%p, cfg=%p, err=%d\n",
                (void*)lfs, (void*)dir, (void*)lfs->cfg, err);
        fflush(f);
    }

    /* The original statement under scrutiny. According to the reported
       violation, LFS_UNLOCK(cfg) is defined as ((void)cfg) and therefore
       only evaluates its argument. We keep the original call to preserve
       behaviour while logging before/after values. */
    LFS_UNLOCK(lfs->cfg);

    if (f) {
        fprintf(f, "lfs_dir_close: after  LFS_UNLOCK: cfg=%p\n", (void*)lfs->cfg);
        fclose(f);
    }

    return err;
}
