static int lfs_dir_getinfo(lfs_t *lfs, lfs_mdir_t *dir,
        uint16_t id, struct lfs_info *info) {
    /* Logging: record entry and key state */
    {
        FILE *f = fopen("/tmp/logs/353.txt", "a");
        if (f) {
            fprintf(f, "ENTER lfs_dir_getinfo: id=%u, lfs=%p, dir=%p, info=%p, lfs->name_max=%d, sizeof(info->name)=%zu\n",
                    (unsigned)id, (void*)lfs, (void*)dir, (void*)info, (int)lfs->name_max, sizeof(info->name));
            fclose(f);
        }
    }

    if (id == 0x3ff) {
        /* special case for root */
        {
            FILE *f = fopen("/tmp/logs/353.txt", "a");
            if (f) {
                fprintf(f, "CASE root: id==0x3ff -> strcpy(info->name, \"/\")\n");
                fprintf(f, "BEFORE strcpy: info=%p, sizeof(info->name)=%zu, lfs->name_max=%d\n",
                        (void*)info, sizeof(info->name), (int)lfs->name_max);
                fclose(f);
            }
        }

        strcpy(info->name, "/");
        info->type = LFS_TYPE_DIR;

        {
            FILE *f = fopen("/tmp/logs/353.txt", "a");
            if (f) {
                fprintf(f, "AFTER strcpy: info->name(printed up to sizeof)='%.*s'\n",
                        (int)sizeof(info->name), info->name);
                fprintf(f, "RETURN 0 (root)\n");
                fclose(f);
            }
        }
        return 0;
    }

    lfs_stag_t tag = lfs_dir_get(lfs, dir, LFS_MKTAG(0x780, 0x3ff, 0),
            LFS_MKTAG(LFS_TYPE_NAME, id, lfs->name_max+1), info->name);

    {
        FILE *f = fopen("/tmp/logs/353.txt", "a");
        if (f) {
            fprintf(f, "AFTER lfs_dir_get(name): tag=%d\n", (int)tag);
            /* print the name with a bounded precision to avoid overreads */
            fprintf(f, "info->name (bounded print up to lfs->name_max+1 or sizeof): '%.*s'\n",
                    (int)((lfs->name_max + 1 <= (int)sizeof(info->name)) ? lfs->name_max + 1 : (int)sizeof(info->name)),
                    info->name);
            fclose(f);
        }
    }

    if (tag < 0) {
        FILE *f = fopen("/tmp/logs/353.txt", "a");
        if (f) {
            fprintf(f, "ERROR: lfs_dir_get(name) returned tag=%d -> returning %d\n", (int)tag, (int)tag);
            fclose(f);
        }
        return (int)tag;
    }

    info->type = lfs_tag_type3(tag);

    struct lfs_ctz ctz;
    tag = lfs_dir_get(lfs, dir, LFS_MKTAG(0x700, 0x3ff, 0),
            LFS_MKTAG(LFS_TYPE_STRUCT, id, sizeof(ctz)), &ctz);

    {
        FILE *f = fopen("/tmp/logs/353.txt", "a");
        if (f) {
            fprintf(f, "AFTER lfs_dir_get(struct): tag=%d\n", (int)tag);
            fclose(f);
        }
    }

    if (tag < 0) {
        FILE *f = fopen("/tmp/logs/353.txt", "a");
        if (f) {
            fprintf(f, "ERROR: lfs_dir_get(struct) returned tag=%d -> returning %d\n", (int)tag, (int)tag);
            fclose(f);
        }
        return (int)tag;
    }
    lfs_ctz_fromle32(&ctz);

    if (lfs_tag_type3(tag) == LFS_TYPE_CTZSTRUCT) {
        info->size = ctz.size;
    } else if (lfs_tag_type3(tag) == LFS_TYPE_INLINESTRUCT) {
        info->size = lfs_tag_size(tag);
    }

    {
        FILE *f = fopen("/tmp/logs/353.txt", "a");
        if (f) {
            fprintf(f, "EXIT lfs_dir_getinfo: id=%u, info=%p, info->type=%d, info->size=%llu\n",
                    (unsigned)id, (void*)info, (int)info->type, (unsigned long long)info->size);
            fclose(f);
        }
    }

    return 0;
}
