int lfs_dir_open(lfs_t *lfs, lfs_dir_t *dir, const char *path) {
    /* Probe logging: append control flow and state info to /tmp/logs/69.txt */
    int err = LFS_LOCK(lfs->cfg);

    /* Minimal, robust logging: try to open the log file and write a few lines. If logging fails,
       we silently continue to preserve original behavior. */
    {
        FILE *log = fopen("/tmp/logs/69.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_open ENTER: lfs=%p dir=%p path=%s\n",
                    (void *)lfs, (void *)dir, (path != NULL) ? path : "(null)");
            fprintf(log, "after LFS_LOCK: err=%d\n", err);
            fclose(log);
        }
    }

    if (err) {
        /* Log the early-return path */
        FILE *log = fopen("/tmp/logs/69.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_open EARLY RETURN due to err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_open(%p, %p, \"%s\")", (void*)lfs, (void*)dir, path);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)dir));

    err = lfs_dir_open_(lfs, dir, path);

    /* Log the outcome from the internal open call */
    {
        FILE *log = fopen("/tmp/logs/69.txt", "a");
        if (log) {
            fprintf(log, "after lfs_dir_open_: err=%d\n", err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_dir_open -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Final log indicating unlock and return value */
    {
        FILE *log = fopen("/tmp/logs/69.txt", "a");
        if (log) {
            fprintf(log, "LFS_UNLOCK done, returning %d\n", err);
            fclose(log);
        }
    }

    return err;
}
