int lfs_dir_open(lfs_t *lfs, lfs_dir_t *dir, const char *path) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry and lock result */
    {
        FILE *__lfs_log_f = fopen("/tmp/logs/66.txt", "a");
        if (__lfs_log_f) {
            fprintf(__lfs_log_f,
                "lfs_dir_open: entry lfs=%p dir=%p path=\"%s\" LFS_LOCK returned=%d\n",
                (void*)lfs, (void*)dir, (path != NULL) ? path : "(null)", err);
            fprintf(__lfs_log_f, "lfs_dir_open: branch if(err) will be %s\n", (err ? "true" : "false"));
            fclose(__lfs_log_f);
        }
    }

    if (err) {
        /* Probe: log early return due to lock error */
        FILE *__lfs_log_f2 = fopen("/tmp/logs/66.txt", "a");
        if (__lfs_log_f2) {
            fprintf(__lfs_log_f2, "lfs_dir_open: early return due to err=%d\n", err);
            fclose(__lfs_log_f2);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_open(%p, %p, \"%s\")", (void*)lfs, (void*)dir, path);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)dir));

    err = lfs_dir_open_(lfs, dir, path);

    /* Probe: log result of lfs_dir_open_ and unlocking */
    {
        FILE *__lfs_log_f3 = fopen("/tmp/logs/66.txt", "a");
        if (__lfs_log_f3) {
            fprintf(__lfs_log_f3, "lfs_dir_open: lfs_dir_open_ returned=%d\n", err);
            fclose(__lfs_log_f3);
        }
    }

    LFS_TRACE("lfs_dir_open -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Probe: final return log */
    {
        FILE *__lfs_log_f4 = fopen("/tmp/logs/66.txt", "a");
        if (__lfs_log_f4) {
            fprintf(__lfs_log_f4, "lfs_dir_open: exit returning=%d\n", err);
            fclose(__lfs_log_f4);
        }
    }

    return err;
}
