int lfs_dir_open(lfs_t *lfs, lfs_dir_t *dir, const char *path) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open log file for appending control-flow and state information. */
    FILE *log = fopen("/tmp/logs/68.txt", "a");
    if (log) {
        fprintf(log, "lfs_dir_open ENTER: lfs=%p dir=%p path=\"%s\"\n",
                (void*)lfs, (void*)dir, path ? path : "(null)");
        fprintf(log, "Called LFS_LOCK(lfs->cfg); returned err=%d\n", err);
        /* Log the address of the cfg member to show that the cfg expression was evaluated.
           Using &lfs->cfg is safe even if cfg is not a pointer type. */
        fprintf(log, "address of lfs->cfg: %p\n", (void*)(lfs ? (void*)&(lfs->cfg) : NULL));
        fflush(log);
    }

    if (err) {
        if (log) {
            fprintf(log, "lfs_dir_open: LFS_LOCK indicated error %d, returning early\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_open(%p, %p, \"%s\")", (void*)lfs, (void*)dir, path);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)dir));

    err = lfs_dir_open_(lfs, dir, path);

    if (log) {
        fprintf(log, "lfs_dir_open_: returned %d\n", err);
        fflush(log);
    }

    LFS_TRACE("lfs_dir_open -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (log) {
        fprintf(log, "Called LFS_UNLOCK(lfs->cfg)\n");
        fprintf(log, "lfs_dir_open EXIT: returning %d\n", err);
        fclose(log);
    }

    return err;
}
