int lfs_dir_open(lfs_t *lfs, lfs_dir_t *dir, const char *path) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log entry and the result of LFS_LOCK so we can observe that it always
       returns 0 in the reported configuration (making the error branch dead). */
    {
        FILE *log = fopen("/tmp/logs/68.txt", "a");
        if (log) {
            fprintf(log, "ENTER lfs_dir_open: lfs=%p dir=%p path=\"%s\" LFS_LOCK_err=%d\n",
                    (void*)lfs, (void*)dir, (path ? path : "(null)"), err);
            fclose(log);
        }
    }

    if (err) {
        /* Log that we're returning early due to lock error. */
        FILE *log = fopen("/tmp/logs/68.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_open: returning early with err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_open(%p, %p, \"%s\")", (void*)lfs, (void*)dir, path);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)dir));

    /* Call the underlying implementation and log its return value. */
    err = lfs_dir_open_(lfs, dir, path);
    {
        FILE *log = fopen("/tmp/logs/68.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_open_: returned %d\n", err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_dir_open -> %d", err);

    /* Log about to call unlock (which in the reported configuration is a no-op). */
    {
        FILE *log = fopen("/tmp/logs/68.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_open: calling LFS_UNLOCK(lfs->cfg)\n");
            fclose(log);
        }
    }
    LFS_UNLOCK(lfs->cfg);
    {
        FILE *log = fopen("/tmp/logs/68.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_open: returned from LFS_UNLOCK(lfs->cfg)\n");
            fclose(log);
        }
    }

    return err;
}
