int lfs_dir_open(lfs_t *lfs, lfs_dir_t *dir, const char *path) {
    int err = LFS_LOCK(lfs->cfg);

    /* Minimal file-based logging to help reproduce/control the violation scenario.
       Do not dereference `path` here (except to test for NULL) to avoid introducing
       new out-of-bounds accesses. */
    FILE *log = fopen("/tmp/logs/63.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_dir_open: lfs=%p dir=%p path_ptr=%p path_is_null=%d lock_err=%d\n",
                (void*)lfs, (void*)dir, (const void*)path, (int)(path == NULL), err);
        fflush(log);
        fclose(log);
    }

    if (err) {
        /* Log early return due to lock failure */
        FILE *elog = fopen("/tmp/logs/63.txt", "a");
        if (elog) {
            fprintf(elog, "lfs_dir_open: early return due to LFS_LOCK -> %d\n", err);
            fflush(elog);
            fclose(elog);
        }
        return err;
    }

    /* Preserve original tracing behavior (may itself print path via %s as before). */
    LFS_TRACE("lfs_dir_open(%p, %p, \"%s\")", (void*)lfs, (void*)dir, path);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)dir));

    /* Log just before delegating to the internal implementation. This captures
       the control point where a NULL or non-terminated `path` would later be
       used by lfs_dir_open_ / lfs_dir_find. */
    log = fopen("/tmp/logs/63.txt", "a");
    if (log) {
        fprintf(log, "BEFORE lfs_dir_open_: calling with path_ptr=%p path_is_null=%d\n",
                (const void*)path, (int)(path == NULL));
        fflush(log);
        fclose(log);
    }

    err = lfs_dir_open_(lfs, dir, path);

    /* Log result and return path to help correlate cause/effect in reproductions. */
    log = fopen("/tmp/logs/63.txt", "a");
    if (log) {
        fprintf(log, "AFTER lfs_dir_open_: result=%d lfs=%p dir=%p path_ptr=%p\n",
                err, (void*)lfs, (void*)dir, (const void*)path);
        fflush(log);
        fclose(log);
    }

    LFS_TRACE("lfs_dir_open -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
