static int lfs_dir_open_(lfs_t *lfs, lfs_dir_t *dir, const char *path) {
    FILE *log = fopen("/tmp/logs/464.txt", "a");
    if (log) {
        fprintf(log, "lfs_dir_open_: entry lfs=%p dir=%p path=%p\n", (void *)lfs, (void *)dir, (void *)path);
        if (path) {
            fprintf(log, "  initial path: ");
            for (int i = 0; i < 64 && path[i]; i++) {
                fputc((unsigned char)path[i], log);
            }
            fputc('\n', log);
        } else {
            fprintf(log, "  initial path: (null)\n");
        }
    }

    lfs_stag_t tag = lfs_dir_find(lfs, &dir->m, &path, NULL);
    if (log) {
        fprintf(log, "  lfs_dir_find returned tag=0x%x path(after)=%p\n", (unsigned)tag, (void *)path);
        if (path) {
            fprintf(log, "  path(after) content (up to 32 bytes): ");
            for (int i = 0; i < 32 && path[i]; i++) {
                fputc((unsigned char)path[i], log);
            }
            fputc('\n', log);
        } else {
            fprintf(log, "  path(after) content: (null)\n");
        }
    }

    if (tag < 0) {
        if (log) { fprintf(log, "  tag < 0, returning %d\n", (int)tag); fclose(log); }
        return tag;
    }

    if (lfs_tag_type3(tag) != LFS_TYPE_DIR) {
        if (log) { fprintf(log, "  tag type is not DIR, returning LFS_ERR_NOTDIR\n"); fclose(log); }
        return LFS_ERR_NOTDIR;
    }

    lfs_block_t pair[2];
    if (lfs_tag_id(tag) == 0x3ff) {
        if (log) { fprintf(log, "  tag id is 0x3ff -> root dir\n"); }
        pair[0] = lfs->root[0];
        pair[1] = lfs->root[1];
    } else {
        if (log) { fprintf(log, "  getting dir pair from parent\n"); }
        lfs_stag_t res = lfs_dir_get(lfs, &dir->m, LFS_MKTAG(0x700, 0x3ff, 0),
                LFS_MKTAG(LFS_TYPE_STRUCT, lfs_tag_id(tag), 8), pair);
        if (log) { fprintf(log, "  lfs_dir_get returned %d pair[0]=%u pair[1]=%u\n", (int)res, (unsigned)pair[0], (unsigned)pair[1]); }
        if (res < 0) {
            if (log) { fprintf(log, "  lfs_dir_get error %d, returning\n", (int)res); fclose(log); }
            return res;
        }
        lfs_pair_fromle32(pair);
        if (log) { fprintf(log, "  after lfs_pair_fromle32 pair[0]=%u pair[1]=%u\n", (unsigned)pair[0], (unsigned)pair[1]); }
    }

    // fetch first pair
    int err = lfs_dir_fetch(lfs, &dir->m, pair);
    if (log) { fprintf(log, "  lfs_dir_fetch returned %d\n", err); }
    if (err) {
        if (log) { fprintf(log, "  fetching error %d returning\n", err); fclose(log); }
        return err;
    }

    // setup entry
    dir->head[0] = dir->m.pair[0];
    dir->head[1] = dir->m.pair[1];
    dir->id = 0;
    dir->pos = 0;

    if (log) {
        fprintf(log, "  set head[0]=%u head[1]=%u id=%u pos=%u\n",
                (unsigned)dir->head[0], (unsigned)dir->head[1], (unsigned)dir->id, (unsigned)dir->pos);
    }

    // add to list of mdirs
    dir->type = LFS_TYPE_DIR;
    lfs_mlist_append(lfs, (struct lfs_mlist *)dir);

    if (log) { fprintf(log, "  appended to mlist and returning 0\n"); fclose(log); }
    return 0;
}
