int lfs_dir_read(lfs_t *lfs, lfs_dir_t *dir, struct lfs_info *info) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log entry and early return */
        FILE *logf = fopen("/tmp/logs/898.txt", "a");
        if (logf) {
            fprintf(logf, "lfs_dir_read ENTER (early return): lfs=%p dir=%p info=%p LFS_LOCK err=%d\n",
                    (void*)lfs, (void*)dir, (void*)info, err);
            fclose(logf);
        }
        return err;
    }

    /* Log that we entered the function */
    FILE *logf = fopen("/tmp/logs/898.txt", "a");
    if (logf) {
        fprintf(logf, "lfs_dir_read ENTER: lfs=%p dir=%p info=%p\n",
                (void*)lfs, (void*)dir, (void*)info);
        fclose(logf);
    }

    LFS_TRACE("lfs_dir_read(%p, %p, %p)",
            (void*)lfs, (void*)dir, (void*)info);

    err = lfs_dir_read_(lfs, dir, info);

    LFS_TRACE("lfs_dir_read -> %d", err);

    /* Log state just before calling LFS_UNLOCK to capture control flow/state
       and to provide evidence that LFS_UNLOCK is invoked here (even if it's a no-op). */
    logf = fopen("/tmp/logs/898.txt", "a");
    if (logf) {
        /* lfs->cfg is typically a pointer to the config structure; print its address. */
        fprintf(logf, "lfs_dir_read BEFORE LFS_UNLOCK: lfs=%p cfg=%p err=%d\n",
                (void*)lfs, (void*)lfs->cfg, err);
        fclose(logf);
    }

    LFS_UNLOCK(lfs->cfg);

    /* Log immediately after calling LFS_UNLOCK to show that the call was reached. */
    logf = fopen("/tmp/logs/898.txt", "a");
    if (logf) {
        fprintf(logf, "lfs_dir_read AFTER LFS_UNLOCK: lfs=%p cfg=%p (note: LFS_UNLOCK may be a no-op)\n",
                (void*)lfs, (void*)lfs->cfg);
        fclose(logf);
    }

    return err;
}
