static int lfs_dir_read_(lfs_t *lfs, lfs_dir_t *dir, struct lfs_info *info) {
    /* lightweight logging to help reproduce Rule 8.13 scenario */
    FILE *log = fopen("/tmp/logs/885.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_dir_read_: lfs=%p dir=%p info=%p dir->pos=%d dir->id=%d\n",
                (void*)lfs, (void*)dir, (void*)info,
                (int)dir->pos, (int)dir->id);
        fclose(log);
    }

    memset(info, 0, sizeof(*info));

    log = fopen("/tmp/logs/885.txt", "a");
    if (log) {
        fprintf(log, "after memset: info cleared\n");
        fclose(log);
    }

    // special offset for '.' and '..'
    if (dir->pos == 0) {
        info->type = LFS_TYPE_DIR;
        strcpy(info->name, ".");
        dir->pos += 1;

        log = fopen("/tmp/logs/885.txt", "a");
        if (log) {
            fprintf(log, "BRANCH pos==0: set name='.' pos=%d\n", (int)dir->pos);
            fclose(log);
        }

        return true;
    } else if (dir->pos == 1) {
        info->type = LFS_TYPE_DIR;
        strcpy(info->name, "..");
        dir->pos += 1;

        log = fopen("/tmp/logs/885.txt", "a");
        if (log) {
            fprintf(log, "BRANCH pos==1: set name='..' pos=%d\n", (int)dir->pos);
            fclose(log);
        }

        return true;
    }

    while (true) {
        log = fopen("/tmp/logs/885.txt", "a");
        if (log) {
            fprintf(log, "LOOP: dir->id=%d dir->m.count=%d dir->m.split=%d\n",
                    (int)dir->id, (int)dir->m.count, (int)dir->m.split);
            fclose(log);
        }

        if (dir->id == dir->m.count) {
            if (!dir->m.split) {
                log = fopen("/tmp/logs/885.txt", "a");
                if (log) {
                    fprintf(log, "NO SPLIT and id==count: returning false\n");
                    fclose(log);
                }
                return false;
            }

            int err = lfs_dir_fetch(lfs, &dir->m, dir->m.tail);

            log = fopen("/tmp/logs/885.txt", "a");
            if (log) {
                fprintf(log, "lfs_dir_fetch returned %d (called with lfs=%p m.tail=%d)\n",
                        err, (void*)lfs, (int)dir->m.tail);
                fclose(log);
            }

            if (err) {
                log = fopen("/tmp/logs/885.txt", "a");
                if (log) {
                    fprintf(log, "lfs_dir_fetch error: returning %d\n", err);
                    fclose(log);
                }
                return err;
            }

            dir->id = 0;
        }

        int err = lfs_dir_getinfo(lfs, &dir->m, dir->id, info);

        log = fopen("/tmp/logs/885.txt", "a");
        if (log) {
            fprintf(log, "lfs_dir_getinfo returned %d for id=%d\n", err, (int)dir->id);
            if (err == LFS_ERR_NOENT) {
                fprintf(log, "lfs_dir_getinfo: entry is NOENT, skipping\n");
            } else if (err) == 0) {
                fprintf(log, "lfs_dir_getinfo: found entry name='%s' type=%d\n", info->name, info->type);
            }
            fclose(log);
        }

        if (err && err != LFS_ERR_NOENT) {
            log = fopen("/tmp/logs/885.txt", "a");
            if (log) {
                fprintf(log, "unexpected error from lfs_dir_getinfo: %d, returning\n", err);
                fclose(log);
            }
            return err;
        }

        dir->id += 1;
        if (err != LFS_ERR_NOENT) {
            break;
        }
    }

    dir->pos += 1;

    log = fopen("/tmp/logs/885.txt", "a");
    if (log) {
        fprintf(log, "EXIT returning true: pos=%d id=%d name='%s' type=%d\n",
                (int)dir->pos, (int)dir->id, info->name, info->type);
        fclose(log);
    }

    return true;
}
