int lfs_dir_rewind(lfs_t *lfs, lfs_dir_t *dir) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe log file to capture control flow and state for Rule 2.1 investigation. */
    {
        FILE *log = fopen("/tmp/logs/1066.txt", "a");
        if (log) {
            fprintf(log, "ENTER: lfs_dir_rewind(lfs=%p, dir=%p)\n", (void*)lfs, (void*)dir);
            fprintf(log, "AFTER LFS_LOCK: err=%d\n", err);
            fclose(log);
        }
    }

    if (err) {
        /* Log the branch that is reported unreachable in the violation scenario. */
        FILE *log = fopen("/tmp/logs/1066.txt", "a");
        if (log) {
            fprintf(log, "BRANCH TAKEN: if (err) => returning err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_rewind(%p, %p)", (void*)lfs, (void*)dir);

    err = lfs_dir_rewind_(lfs, dir);

    /* Log the result of the inner call and the unlock action. */
    {
        FILE *log = fopen("/tmp/logs/1066.txt", "a");
        if (log) {
            fprintf(log, "AFTER lfs_dir_rewind_: err=%d\n", err);
            fprintf(log, "About to call LFS_UNLOCK with cfg=%p\n", (void*)lfs->cfg);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_dir_rewind -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
