int lfs_dir_rewind(lfs_t *lfs, lfs_dir_t *dir) {
    /* Local include to ensure FILE and fopen/fprintf are available when
       this function is injected into various translation units. This is
       allowed as the preprocessor handles includes before compilation. */
    #include <stdio.h>

    int err = LFS_LOCK(lfs->cfg);

    /* Open log file in append mode so repeated test runs accumulate evidence. */
    FILE *log = fopen("/tmp/logs/1068.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_dir_rewind: lfs=%p dir=%p cfg=%p lock_err=%d\n",
                (void*)lfs, (void*)dir, (void*)(lfs ? lfs->cfg : NULL), err);
    }

    if (err) {
        if (log) {
            fprintf(log, "LFS_LOCK failed, returning %d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_rewind(%p, %p)", (void*)lfs, (void*)dir);

    /* Capture state before calling the internal rewind helper. */
    if (log) {
        fprintf(log, "Calling lfs_dir_rewind_: current_dir=%p\n", (void*)dir);
    }

    err = lfs_dir_rewind_(lfs, dir);

    if (log) {
        fprintf(log, "Returned from lfs_dir_rewind_: err=%d\n", err);
    }

    LFS_TRACE("lfs_dir_rewind -> %d", err);

    /* Log cfg pointer/state immediately before and after LFS_UNLOCK to
       provide runtime evidence whether the unlock operation has any effect.
       If LFS_UNLOCK is a no-op (e.g. expands to ((void)cfg)), the cfg
       pointer and observable state will be unchanged. */
    if (log) {
        void *cfg_before = (void*)(lfs ? lfs->cfg : NULL);
        fprintf(log, "before LFS_UNLOCK cfg=%p\n", cfg_before);
    }

    LFS_UNLOCK(lfs->cfg);

    if (log) {
        void *cfg_after = (void*)(lfs ? lfs->cfg : NULL);
        fprintf(log, "after LFS_UNLOCK cfg=%p\n", cfg_after);
        fprintf(log, "lfs_dir_rewind returning %d\n", err);
        fclose(log);
    }

    return err;
}
