int lfs_dir_rewind(lfs_t *lfs, lfs_dir_t *dir) {
    int err = LFS_LOCK(lfs->cfg);

    /* Log entry and lock result to persistent file for reproducing dead-code evidence */
    {
        FILE *fp = fopen("/tmp/logs/1068.txt", "a");
        if (fp) {
            fprintf(fp, "ENTER lfs_dir_rewind: lfs=%p dir=%p\n", (void*)lfs, (void*)dir);
            fprintf(fp, "AFTER LFS_LOCK: err=%d\n", err);
            fclose(fp);
        } else {
            /* Best-effort trace if file cannot be opened */
            LFS_TRACE("lfs_dir_rewind: failed to open log file /tmp/logs/1068.txt");
        }
    }

    if (err) {
        return err;
    }

    LFS_TRACE("lfs_dir_rewind(%p, %p)", (void*)lfs, (void*)dir);

    err = lfs_dir_rewind_(lfs, dir);

    /* Log result of inner rewind and intent to call unlock */
    {
        FILE *fp = fopen("/tmp/logs/1068.txt", "a");
        if (fp) {
            fprintf(fp, "BEFORE LFS_UNLOCK: inner err=%d\n", err);
            fprintf(fp, "CALLING LFS_UNLOCK with cfg=%p\n", (void*)lfs->cfg);
            fclose(fp);
        } else {
            LFS_TRACE("lfs_dir_rewind: failed to open log file /tmp/logs/1068.txt");
        }
    }

    LFS_TRACE("lfs_dir_rewind -> %d", err);

    /* The call below is suspected to be a no-op (e.g. #define LFS_UNLOCK(cfg) ((void)cfg)).
       We still invoke it to preserve original behavior while logging above allows
       reproduction/evidence that the call occurred. */
    LFS_UNLOCK(lfs->cfg);

    /* Log after unlock to show control passed beyond the call (if reachable) */
    {
        FILE *fp = fopen("/tmp/logs/1068.txt", "a");
        if (fp) {
            fprintf(fp, "AFTER LFS_UNLOCK: returned err=%d\n", err);
            fprintf(fp, "EXIT lfs_dir_rewind\n");
            fclose(fp);
        } else {
            LFS_TRACE("lfs_dir_rewind: failed to open log file /tmp/logs/1068.txt");
        }
    }

    return err;
}
