int lfs_dir_seek(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open probe log (append). If this fails, fall back to LFS_TRACE so tests
       still observe some logging. */
    FILE *probe = fopen("/tmp/logs/909.txt", "a");
    if (probe) {
        /* Print entry and state after locking attempt. Use conditional to avoid
           dereferencing lfs if it's NULL (matching original code behavior as
           closely as possible while being slightly defensive for logging). */
        fprintf(probe, "lfs_dir_seek ENTRY: lfs=%p dir=%p off=%" PRIu32 " cfg=%p err_after_lock=%d\n",
                (void*)lfs, (void*)dir, off, (void*)(lfs ? lfs->cfg : NULL), err);
        fflush(probe);
    } else {
        LFS_TRACE("lfs_dir_seek ENTRY: lfs=%p dir=%p off=%"PRIu32" cfg=%p err_after_lock=%d",
                (void*)lfs, (void*)dir, off, (void*)(lfs ? lfs->cfg : NULL), err);
    }

    if (err) {
        if (probe) {
            fprintf(probe, "lfs_dir_seek: lock reported err=%d, returning immediately\n", err);
            fclose(probe);
        } else {
            LFS_TRACE("lfs_dir_seek: lock reported err=%d, returning immediately", err);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_seek(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)dir, off);

    if (probe) {
        fprintf(probe, "lfs_dir_seek: lock succeeded, calling lfs_dir_seek_\n");
        fflush(probe);
    }

    err = lfs_dir_seek_(lfs, dir, off);

    if (probe) {
        fprintf(probe, "lfs_dir_seek_: returned %d\n", err);
        fflush(probe);
    }

    LFS_TRACE("lfs_dir_seek -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (probe) {
        fprintf(probe, "lfs_dir_seek: unlocked, returning %d\n", err);
        fclose(probe);
    }

    return err;
}
