int lfs_dir_seek(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    /* Open log file for appending; do not alter behavior if logging fails. */
    FILE *log = fopen("/tmp/logs/906.txt", "a");

    /* Preserve original lock semantics */
    int err = LFS_LOCK(lfs->cfg);

    if (log) {
        /* Log entry and immediate lock result so callers can see if lock makes err == 0 */
        (void)fprintf(log,
            "ENTER lfs_dir_seek: lfs=%p, dir=%p, off=%llu, LFS_LOCK_result=%d\n",
            (void*)lfs, (void*)dir, (unsigned long long)off, err);
        (void)fflush(log);
    }

    if (err) {
        /* Log and return exactly as original would do on lock error */
        if (log) {
            (void)fprintf(log, "lfs_dir_seek: early return due to LFS_LOCK -> %d\n", err);
            (void)fflush(log);
            (void)fclose(log);
        }
        return err;
    }

    /* Original trace kept for compatibility */
    LFS_TRACE("lfs_dir_seek(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)dir, off);

    if (log) {
        (void)fprintf(log, "lfs_dir_seek: LFS_LOCK succeeded, calling lfs_dir_seek_\n");
        (void)fflush(log);
    }

    err = lfs_dir_seek_(lfs, dir, off);

    if (log) {
        (void)fprintf(log, "lfs_dir_seek_: returned %d\n", err);
        (void)fflush(log);
    }

    LFS_TRACE("lfs_dir_seek -> %d", err);

    LFS_UNLOCK(lfs->cfg);

    if (log) {
        (void)fprintf(log, "lfs_dir_seek: unlocked, returning %d\n", err);
        (void)fclose(log);
    }

    return err;
}
