int lfs_dir_seek(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    int err = LFS_LOCK(lfs->cfg);

    /* Open a log file to record control flow and state for debugging the
       potential dead-code (no-op) expansion of LFS_UNLOCK. Use append mode
       so multiple calls accumulate. If opening fails, silently continue so
       behaviour is preserved. */
    FILE *logf = fopen("/tmp/logs/908.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_dir_seek: lfs=%p dir=%p off=%" PRIu32 "\n",
                (void*)lfs, (void*)dir, off);
        fprintf(logf, "  -> after LFS_LOCK: err=%d\n", err);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "EXIT lfs_dir_seek (early return from LFS_LOCK): err=%d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_seek(%p, %p, %"PRIu32")",
            (void*)lfs, (void*)dir, off);

    /* Call the underlying implementation and record its result. */
    err = lfs_dir_seek_(lfs, dir, off);

    if (logf) {
        fprintf(logf, "  -> after lfs_dir_seek_: err=%d\n", err);
        fflush(logf);
    }

    /* Log intent to call unlock and the cfg pointer value so that a run
       where LFS_UNLOCK is a no-op (e.g. ((void)cfg)) can be evidenced by
       showing the unlock call occurred but produced no observable change. */
    if (logf) {
        fprintf(logf, "  -> calling LFS_UNLOCK with cfg=%p\n", (void*)lfs->cfg);
        fflush(logf);
    }

    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "  -> returned from LFS_UNLOCK (no further check performed)\n");
        fprintf(logf, "EXIT lfs_dir_seek: returning err=%d\n", err);
        fclose(logf);
    }

    LFS_TRACE("lfs_dir_seek -> %d", err);
    return err;
}
