static int lfs_dir_seek_(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    /* Logging probe to capture control flow and state for Rule 17.8 violation evidence. */
    FILE *f = fopen("/tmp/logs/532.txt", "a");
    if (f) {
        /* Log entry and initial state */
        fprintf(f, "ENTER lfs_dir_seek_: lfs=%p dir=%p initial_off=%lld dir->pos=%lld dir->id=%d m.count=%d m.split=%d\n",
                (void*)lfs, (void*)dir, (long long)off,
                (long long)dir->pos, (int)dir->id,
                (int)dir->m.count, (int)dir->m.split);
        fflush(f);
    }

    /* simply walk from head dir */
    int err = lfs_dir_rewind_(lfs, dir);
    if (f) {
        fprintf(f, "after lfs_dir_rewind_: err=%d\n", err);
        fflush(f);
    }
    if (err) {
        if (f) {
            fprintf(f, "RETURN lfs_dir_seek_ (early): %d\n", err);
            fclose(f);
        }
        return err;
    }

    /* first two for ./.. */
    dir->pos = lfs_min(2, off);
    if (f) {
        fprintf(f, "after set dir->pos = lfs_min(2, off): dir->pos=%lld (assigned), off(before_sub)=%lld\n",
                (long long)dir->pos, (long long)off);
        fflush(f);
    }

    /* Violation point: parameter 'off' is modified here */
    off -= dir->pos;
    if (f) {
        fprintf(f, "after off -= dir->pos: off=%lld\n", (long long)off);
        fflush(f);
    }

    /* skip superblock entry */
    dir->id = (off > 0 && lfs_pair_cmp(dir->head, lfs->root) == 0);
    if (f) {
        fprintf(f, "after skip superblock: dir->id=%d (bool), off=%lld\n", (int)dir->id, (long long)off);
        fflush(f);
    }

    while (off > 0) {
        if (f) {
            fprintf(f, "loop top: off=%lld dir->id=%d dir->m.count=%d dir->m.split=%d\n",
                    (long long)off, (int)dir->id, (int)dir->m.count, (int)dir->m.split);
            fflush(f);
        }

        if (dir->id == dir->m.count) {
            if (!dir->m.split) {
                if (f) {
                    fprintf(f, "RETURN lfs_dir_seek_ (INVAL): %d -- dir->id == dir->m.count and not split\n", LFS_ERR_INVAL);
                    fclose(f);
                }
                return LFS_ERR_INVAL;
            }

            err = lfs_dir_fetch(lfs, &dir->m, dir->m.tail);
            if (f) {
                fprintf(f, "after lfs_dir_fetch: err=%d\n", err);
                fflush(f);
            }
            if (err) {
                if (f) {
                    fprintf(f, "RETURN lfs_dir_seek_ (fetch err): %d\n", err);
                    fclose(f);
                }
                return err;
            }

            dir->id = 0;
            if (f) {
                fprintf(f, "after fetching tail: dir->id reset to 0, off=%lld dir->pos=%lld\n",
                        (long long)off, (long long)dir->pos);
                fflush(f);
            }
        }

        int diff = lfs_min(dir->m.count - dir->id, off);
        if (f) {
            fprintf(f, "computed diff = lfs_min(dir->m.count - dir->id, off) = %d\n", diff);
            fflush(f);
        }
        dir->id += diff;
        dir->pos += diff;
        off -= diff;

        if (f) {
            fprintf(f, "after advancing: diff=%d dir->id=%d dir->pos=%lld off=%lld\n",
                    diff, (int)dir->id, (long long)dir->pos, (long long)off);
            fflush(f);
        }
    }

    if (f) {
        fprintf(f, "EXIT lfs_dir_seek_: returning 0 final_off=%lld dir->pos=%lld dir->id=%d\n",
                (long long)off, (long long)dir->pos, (int)dir->id);
        fclose(f);
    }

    return 0;
}
