static int lfs_dir_seek_(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    /* Logging to help reproduce modification of parameter 'off' */
    FILE *log = fopen("/tmp/logs/532.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_dir_seek_: lfs=%p dir=%p initial off=%lld\n",
                (void *)lfs, (void *)dir, (long long)off);
    }

    // simply walk from head dir
    int err = lfs_dir_rewind_(lfs, dir);
    if (err) {
        if (log) {
            fprintf(log, "lfs_dir_rewind_ returned err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    if (log) {
        fprintf(log, "after rewind: dir->pos=%lld dir->id=%d\n",
                (long long)dir->pos, (int)dir->id);
    }

    // first two for ./..
    dir->pos = lfs_min(2, off);
    if (log) {
        fprintf(log, "dir->pos = lfs_min(2, off) => %lld\n", (long long)dir->pos);
    }

    {
        lfs_off_t old_off = off;
        off -= dir->pos; /* <- parameter 'off' is modified (intended violation) */
        if (log) {
            fprintf(log, "off adjusted: old_off=%lld dir->pos=%lld new_off=%lld\n",
                    (long long)old_off, (long long)dir->pos, (long long)off);
        }
    }

    /* skip superblock entry */
    int cmp = lfs_pair_cmp(dir->head, lfs->root);
    dir->id = (off > 0 && cmp == 0);
    if (log) {
        fprintf(log, "dir->id set to %d (off>0=%d, paircmp=%d)\n",
                (int)dir->id, off > 0 ? 1 : 0, cmp);
    }

    while (off > 0) {
        if (log) {
            fprintf(log, "while(off>0) iteration: off=%lld dir->id=%d dir->m.count=%d dir->m.split=%d\n",
                    (long long)off, (int)dir->id, (int)dir->m.count, (int)dir->m.split);
        }

        if (dir->id == dir->m.count) {
            if (!dir->m.split) {
                if (log) {
                    fprintf(log, "invalid: dir->id == dir->m.count and !dir->m.split -> return LFS_ERR_INVAL\n");
                    fclose(log);
                }
                return LFS_ERR_INVAL;
            }

            err = lfs_dir_fetch(lfs, &dir->m, dir->m.tail);
            if (err) {
                if (log) {
                    fprintf(log, "lfs_dir_fetch returned err=%d\n", err);
                    fclose(log);
                }
                return err;
            }

            dir->id = 0;
            if (log) {
                fprintf(log, "fetched next block: dir->id reset to 0\n");
            }
        }

        int diff = lfs_min(dir->m.count - dir->id, off);
        if (log) {
            fprintf(log, "computed diff=%d (min(dir->m.count - dir->id, off))\n", diff);
        }

        dir->id += diff;
        dir->pos += diff;
        if (log) {
            fprintf(log, "updated dir->id=%d dir->pos=%lld (before subtracting diff from off)\n",
                    (int)dir->id, (long long)dir->pos);
        }

        off -= diff; /* <- parameter 'off' is modified again */
        if (log) {
            fprintf(log, "off decreased by diff=%d new off=%lld\n", diff, (long long)off);
        }
    }

    if (log) {
        fprintf(log, "EXIT lfs_dir_seek_: returning 0 final dir->pos=%lld dir->id=%d off=%lld\n",
                (long long)dir->pos, (int)dir->id, (long long)off);
        fclose(log);
    }

    return 0;
}
