static int lfs_dir_seek_(lfs_t *lfs, lfs_dir_t *dir, lfs_off_t off) {
    FILE *f = fopen("/tmp/logs/532.txt", "a");
    if (f) {
        fprintf(f, "ENTER lfs_dir_seek_: lfs=%p dir=%p off=%lld\n", (void*)lfs, (void*)dir, (long long)off);
    }

    int err = lfs_dir_rewind_(lfs, dir);
    if (f) fprintf(f, "after lfs_dir_rewind_ err=%d\n", err);
    if (err) {
        if (f) { fprintf(f, "RETURN err from rewind: %d\n", err); fclose(f); }
        return err;
    }

    dir->pos = lfs_min(2, off);
    if (f) fprintf(f, "dir->pos set to min(2, off) -> %lld\n", (long long)dir->pos);

    off -= dir->pos;
    if (f) fprintf(f, "after off -= dir->pos -> off=%lld\n", (long long)off);

    int pair_eq = (lfs_pair_cmp(dir->head, lfs->root) == 0);
    dir->id = (off > 0 && pair_eq);
    if (f) fprintf(f, "dir->id set to %d (off=%lld, pair_eq=%d)\n", (int)dir->id, (long long)off, pair_eq);

    while (off > 0) {
        if (f) fprintf(f, "WHILE enter: off=%lld dir->id=%d dir->m.count=%d dir->m.split=%d\n", (long long)off, (int)dir->id, (int)dir->m.count, (int)dir->m.split);

        if (dir->id == dir->m.count) {
            if (!dir->m.split) {
                if (f) { fprintf(f, "ERROR: dir->id == dir->m.count and not split -> RETURN LFS_ERR_INVAL\n"); fclose(f); }
                return LFS_ERR_INVAL;
            }

            err = lfs_dir_fetch(lfs, &dir->m, dir->m.tail);
            if (f) fprintf(f, "after lfs_dir_fetch err=%d\n", err);
            if (err) {
                if (f) { fprintf(f, "RETURN err from fetch: %d\n", err); fclose(f); }
                return err;
            }

            dir->id = 0;
            if (f) fprintf(f, "dir->id reset to 0 after fetch\n");
        }

        int diff = lfs_min(dir->m.count - dir->id, off);
        if (f) fprintf(f, "computed diff = min(%d - %d, %lld) = %d\n", (int)dir->m.count, (int)dir->id, (long long)off, diff);

        dir->id += diff;
        dir->pos += diff;
        off -= diff;

        if (f) fprintf(f, "after updates: dir->id=%d dir->pos=%lld off=%lld\n", (int)dir->id, (long long)dir->pos, (long long)off);
    }

    if (f) { fprintf(f, "EXIT lfs_dir_seek_: return 0\n"); fclose(f); }
    return 0;
}
