static int lfs_dir_split(lfs_t *lfs,
        lfs_mdir_t *dir, const struct lfs_mattr *attrs, int attrcount,
        lfs_mdir_t *source, uint16_t split, uint16_t end) {
    FILE *f = fopen("/tmp/logs/1050.txt", "a");
    if (f) {
        fprintf(f, "ENTER lfs_dir_split: lfs=%p dir=%p source=%p split=%u end=%u attrcount=%d dir->split=%d dir->pair[0]=%llu dir->pair[1]=%llu\n",
                (void*)lfs, (void*)dir, (void*)source, (unsigned)split, (unsigned)end, attrcount, (int)dir->split,
                (unsigned long long)dir->pair[0], (unsigned long long)dir->pair[1]);
        fflush(f);
    }

    // create tail metadata pair
    lfs_mdir_t tail;
    int err = lfs_dir_alloc(lfs, &tail);
    if (f) {
        fprintf(f, "after lfs_dir_alloc: err=%d tail_addr=%p\n", err, (void*)&tail);
        fflush(f);
    }
    if (err) {
        if (f) {
            fprintf(f, "EXIT lfs_dir_split: returning err=%d\n", err);
            fclose(f);
        }
        return err;
    }

    tail.split = dir->split;
    tail.tail[0] = dir->tail[0];
    tail.tail[1] = dir->tail[1];
    if (f) {
        fprintf(f, "tail initialized: tail.split=%d tail.tail[0]=%llu tail.tail[1]=%llu\n",
                (int)tail.split,
                (unsigned long long)tail.tail[0], (unsigned long long)tail.tail[1]);
        fflush(f);
    }

    // note we don't care about LFS_OK_RELOCATED
    if (f) {
        fprintf(f, "calling lfs_dir_compact: attrs=%p attrcount=%d source=%p split=%u end=%u\n",
                (void*)attrs, attrcount, (void*)source, (unsigned)split, (unsigned)end);
        fflush(f);
    }
    int res = lfs_dir_compact(lfs, &tail, attrs, attrcount, source, split, end);
    if (f) {
        fprintf(f, "after lfs_dir_compact: res=%d tail.pair[0]=%llu tail.pair[1]=%llu\n",
                res, (unsigned long long)tail.pair[0], (unsigned long long)tail.pair[1]);
        fflush(f);
    }
    if (res < 0) {
        if (f) {
            fprintf(f, "EXIT lfs_dir_split: returning res=%d\n", res);
            fclose(f);
        }
        return res;
    }

    dir->tail[0] = tail.pair[0];
    dir->tail[1] = tail.pair[1];
    dir->split = true;

    if (f) {
        fprintf(f, "updated dir: dir->tail[0]=%llu dir->tail[1]=%llu dir->split=%d\n",
                (unsigned long long)dir->tail[0], (unsigned long long)dir->tail[1], (int)dir->split);
        fflush(f);
    }

    // update root if needed
    if (lfs_pair_cmp(dir->pair, lfs->root) == 0 && split == 0) {
        if (f) {
            fprintf(f, "updating lfs->root: old root[0]=%llu root[1]=%llu tail.pair[0]=%llu tail.pair[1]=%llu\n",
                    (unsigned long long)lfs->root[0], (unsigned long long)lfs->root[1],
                    (unsigned long long)tail.pair[0], (unsigned long long)tail.pair[1]);
            fflush(f);
        }
        lfs->root[0] = tail.pair[0];
        lfs->root[1] = tail.pair[1];
        if (f) {
            fprintf(f, "new lfs->root[0]=%llu lfs->root[1]=%llu\n",
                    (unsigned long long)lfs->root[0], (unsigned long long)lfs->root[1]);
            fflush(f);
        }
    } else {
        if (f) {
            fprintf(f, "no root update: lfs_pair_cmp != 0 or split != 0 (split=%u)\n", (unsigned)split);
            fflush(f);
        }
    }

    if (f) {
        fprintf(f, "EXIT lfs_dir_split: returning 0\n");
        fclose(f);
    }

    return 0;
}
