lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    /* Open log file for appending; if this fails, proceed without logging. */
    FILE *logf = fopen("/tmp/logs/829.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_dir_tell: lfs=%p dir=%p\n", (void*)lfs, (void*)dir);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (logf) {
        fprintf(logf, "LFS_LOCK returned err=%d (cfg=%p)\n", err, (void*)lfs->cfg);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "EARLY RETURN from lfs_dir_tell due to err=%d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_tell(%p, %p)", (void*)lfs, (void*)dir);
    if (logf) {
        fprintf(logf, "calling lfs_dir_tell_()\n");
    }

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    if (logf) {
        /* Print res as long long to avoid format mismatches; cast is safe for logging. */
        fprintf(logf, "lfs_dir_tell_ returned res=%lld\n", (long long)res);
    }

    LFS_TRACE("lfs_dir_tell -> %"PRId32, res);
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "LFS_UNLOCK called, returning res=%lld\n", (long long)res);
        fclose(logf);
    }

    return res;
}
