lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    /* Open log for tracing control flow and state for violation analysis */
    FILE *log = fopen("/tmp/logs/828.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_dir_tell: lfs=%p, dir=%p\n", (void*)lfs, (void*)dir);
        fflush(log);
    }

    /* Original locking call retained to preserve behaviour. This is the expression
       reported in the violation: LFS_LOCK(lfs->cfg) expands to ((void)cfg, 0)
       so the (void)lfs->cfg operand is evaluated but discarded. We log before
       and after to capture that the call occurred and its result. */
    if (log) {
        fprintf(log, "About to evaluate LFS_LOCK(lfs->cfg)\n");
        fflush(log);
    }

    int err = LFS_LOCK(lfs->cfg);

    if (log) {
        fprintf(log, "After LFS_LOCK: err=%d\n", err);
        fflush(log);
    }

    if (err) {
        if (log) {
            fprintf(log, "lfs_dir_tell returning early due to err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    if (log) {
        fprintf(log, "Calling lfs_dir_tell_(lfs, dir)\n");
        fflush(log);
    }

    LFS_TRACE("lfs_dir_tell(%p, %p)", (void*)lfs, (void*)dir);

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    if (log) {
        fprintf(log, "lfs_dir_tell_: res=%" PRId32 "\n", res);
        fflush(log);
    }

    LFS_TRACE("lfs_dir_tell -> %" PRId32, res);

    LFS_UNLOCK(lfs->cfg);

    if (log) {
        fprintf(log, "LFS_UNLOCK called, returning res=%" PRId32 "\n", res);
        fclose(log);
    }

    return res;
}
