lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    /* Preserve original locking semantics */
    int err = LFS_LOCK(lfs->cfg);

    /* Open a log file to record control flow and state for reproduction
       of the reported dead-code scenario. We append so repeated tests
       accumulate evidence. */
    FILE *logf = fopen("/tmp/logs/828.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER: lfs_dir_tell(lfs=%p, dir=%p)\n", (void*)lfs, (void*)dir);
        fprintf(logf, "AFTER LFS_LOCK: err=%d, cfg_ptr=%p\n", err, (void*)lfs->cfg);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "EARLY RETURN: lock error %d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_tell(%p, %p)", (void*)lfs, (void*)dir);

    if (logf) {
        fprintf(logf, "BEFORE lfs_dir_tell_: calling lfs_dir_tell_(lfs=%p, dir=%p)\n", (void*)lfs, (void*)dir);
        fflush(logf);
    }

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    LFS_TRACE("lfs_dir_tell -> %"PRId32, res);

    if (logf) {
        fprintf(logf, "AFTER lfs_dir_tell_: res=%"PRId32"\n", res);
        /* Log the cfg expression used in LFS_UNLOCK to show its evaluation
           has no side-effects in this instance (pointer value only). */
        fprintf(logf, "ABOUT TO CALL LFS_UNLOCK with cfg_ptr=%p\n", (void*)lfs->cfg);
        fflush(logf);
    }

    /* Original unlock call retained to preserve behaviour. In the reported
       scenario LFS_UNLOCK(cfg) expands to ((void)cfg) which is an executed
       no-op; the log above records the evaluated cfg pointer to demonstrate
       that the call only evaluates and discards the expression. */
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "AFTER LFS_UNLOCK (no-op): returning res=%"PRId32"\n", res);
        fclose(logf);
    }

    return res;
}
