lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    /* Probe logging to capture control flow and state for MISRA Rule 2.2 analysis. */
    FILE *logf = fopen("/tmp/logs/828.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_dir_tell: lfs=%p dir=%p\n", (void*)lfs, (void*)dir);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (logf) {
        fprintf(logf, "After LFS_LOCK: err=%d\n", err);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "Branch taken: if (err) is true -> returning err=%d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_tell(%p, %p)", (void*)lfs, (void*)dir);
    if (logf) {
        fprintf(logf, "Proceeding to call lfs_dir_tell_ (err==0)\n");
        fflush(logf);
    }

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    if (logf) {
        fprintf(logf, "lfs_dir_tell_ returned res=%lld\n", (long long)res);
        fflush(logf);
    }

    LFS_TRACE("lfs_dir_tell -> %"PRId32, res);

    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "LFS_UNLOCK called, returning res=%lld\n", (long long)res);
        fclose(logf);
    }

    return res;
}
