lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    /* Preserve original locking semantics */
    int err = LFS_LOCK(lfs->cfg);

    /* Open an append-only log for reproduction and evidence collection. */
    FILE *___lfs_log_f = fopen("/tmp/logs/828.txt", "a");
    if (___lfs_log_f) {
        fprintf(___lfs_log_f, "[lfs_dir_tell] entry: lfs=%p dir=%p cfg=%p LFS_LOCK returned=%d\n",
                (void*)lfs, (void*)dir, (void*)lfs->cfg, err);
        fflush(___lfs_log_f);
    }

    if (err) {
        if (___lfs_log_f) {
            fprintf(___lfs_log_f, "[lfs_dir_tell] early return due to LFS_LOCK error=%d\n", err);
            fclose(___lfs_log_f);
        }
        return err;
    }

    LFS_TRACE("lfs_dir_tell(%p, %p)", (void*)lfs, (void*)dir);

    if (___lfs_log_f) {
        fprintf(___lfs_log_f, "[lfs_dir_tell] calling lfs_dir_tell_ (preserve original behaviour)\n");
        fflush(___lfs_log_f);
    }

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    if (___lfs_log_f) {
        fprintf(___lfs_log_f, "[lfs_dir_tell] lfs_dir_tell_ returned res=%" PRId32 "\n", res);
        fflush(___lfs_log_f);
    }

    LFS_TRACE("lfs_dir_tell -> %"PRId32, res);

    /* Log just before unlock to capture the control point for the reported no-op. */
    if (___lfs_log_f) {
        fprintf(___lfs_log_f, "[lfs_dir_tell] about to call LFS_UNLOCK(lfs->cfg) with cfg=%p\n", (void*)lfs->cfg);
        fflush(___lfs_log_f);
    }

    /* Original call retained. In some configurations this may expand to a no-op; the log
       above/below provides evidence whether the call site was reached and what cfg value
       was supplied. */
    LFS_UNLOCK(lfs->cfg);

    if (___lfs_log_f) {
        fprintf(___lfs_log_f, "[lfs_dir_tell] returned from LFS_UNLOCK(lfs->cfg) (no return value to capture)\n");
        fprintf(___lfs_log_f, "[lfs_dir_tell] final return res=%" PRId32 "\n", res);
        fclose(___lfs_log_f);
    }

    return res;
}
