lfs_soff_t lfs_dir_tell(lfs_t *lfs, lfs_dir_t *dir) {
    /* Open log file early to capture entry and any early returns */
    FILE *log = fopen("/tmp/logs/825.txt", "a");
    if (log) {
        fprintf(log, "ENTER lfs_dir_tell: lfs=%p dir=%p\n", (void*)lfs, (void*)dir);
        if (dir) {
            fprintf(log, "dir != NULL\n");
        } else {
            fprintf(log, "dir == NULL\n");
        }
        fflush(log);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        if (log) {
            fprintf(log, "LFS_LOCK failed: err=%d (returning)\n", err);
            fclose(log);
        }
        return err;
    }
    LFS_TRACE("lfs_dir_tell(%p, %p)", (void*)lfs, (void*)dir);

    if (log) {
        fprintf(log, "After lock: proceeding to call lfs_dir_tell_\n");
        fflush(log);
    }

    lfs_soff_t res = lfs_dir_tell_(lfs, dir);

    if (log) {
        fprintf(log, "lfs_dir_tell_ returned: res=%"PRId32"\n", res);
        fflush(log);
    }

    LFS_TRACE("lfs_dir_tell -> %"PRId32, res);

    if (log) {
        fprintf(log, "About to unlock and return: res=%"PRId32"\n", res);
        fclose(log);
    }
    LFS_UNLOCK(lfs->cfg);
    return res;
}
