int lfs_file_opencfg(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
    /* Probe log file to capture control flow and state for debugging */
    FILE *logf = fopen("/tmp/logs/859.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_file_opencfg: lfs=%p file=%p path=\"%s\" flags=0x%x cfg=%p lfs->cfg=%p\n",
                (void*)lfs, (void*)file, (path?path:"(null)"), (unsigned)flags, (void*)cfg,
                (void*)(lfs ? lfs->cfg : NULL));
        fflush(logf);
    }

    int err = LFS_LOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "After LFS_LOCK: err=%d (expected 0 if LFS_LOCK expands to ((void)cfg,0))\n", err);
        fflush(logf);
    }

    if (err) {
        if (logf) {
            fprintf(logf, "BRANCH: err!=0 -> early return with err=%d\n", err);
            fclose(logf);
        }
        return err;
    }

    LFS_TRACE("lfs_file_opencfg(%p, %p, \"%s\", %x, %p {"
                 ".buffer=%p, .attrs=%p, .attr_count=%"PRIu32"})",
            (void*)lfs, (void*)file, path, (unsigned)flags,
            (void*)cfg, cfg->buffer, (void*)cfg->attrs, cfg->attr_count);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    if (logf) {
        fprintf(logf, "About to call lfs_file_opencfg_: path=\"%s\" flags=0x%x\n",
                (path?path:"(null)"), (unsigned)flags);
        fflush(logf);
    }

    err = lfs_file_opencfg_(lfs, file, path, flags, cfg);

    if (logf) {
        fprintf(logf, "Returned from lfs_file_opencfg_: err=%d\n", err);
        fflush(logf);
    }

    LFS_TRACE("lfs_file_opencfg -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "Performed LFS_UNLOCK(lfs->cfg). Returning err=%d\n", err);
        fclose(logf);
    }
    return err;
}
