int lfs_file_opencfg(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
    int err = LFS_LOCK(lfs->cfg);

    /* Probe: log entry, parameters and the result of LFS_LOCK */
    {
        FILE *log = fopen("/tmp/logs/859.txt", "a");
        if (log) {
            fprintf(log, "ENTER lfs_file_opencfg: lfs=%p file=%p path=%s flags=0x%X cfg=%p\n",
                    (void*)lfs, (void*)file, (path?path:"(null)"), (unsigned)flags, (void*)cfg);
            fprintf(log, "-> LFS_LOCK(lfs->cfg) returned err=%d\n", err);
            if (cfg) {
                fprintf(log, "   cfg->buffer=%p cfg->attrs=%p cfg->attr_count=%llu\n",
                        (void*)cfg->buffer, (void*)cfg->attrs,
                        (unsigned long long)cfg->attr_count);
            } else {
                fprintf(log, "   cfg is NULL\n");
            }
            fclose(log);
        }
    }

    if (err) {
        /* Probe: log early return due to lock failure */
        FILE *log = fopen("/tmp/logs/859.txt", "a");
        if (log) {
            fprintf(log, "EXIT lfs_file_opencfg: returning early due to err=%d\n", err);
            fclose(log);
        }
        return err;
    }

    LFS_TRACE("lfs_file_opencfg(%p, %p, \"%s\", %x, %p {"
                 ".buffer=%p, .attrs=%p, .attr_count=%"PRIu32"})",
            (void*)lfs, (void*)file, path, (unsigned)flags,
            (void*)cfg, cfg->buffer, (void*)cfg->attrs, cfg->attr_count);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    /* Probe: just before calling the core open function */
    {
        FILE *log = fopen("/tmp/logs/859.txt", "a");
        if (log) {
            fprintf(log, "Calling lfs_file_opencfg_: lfs=%p file=%p path=%s flags=0x%X cfg=%p\n",
                    (void*)lfs, (void*)file, (path?path:"(null)"), (unsigned)flags, (void*)cfg);
            fclose(log);
        }
    }

    err = lfs_file_opencfg_(lfs, file, path, flags, cfg);

    /* Probe: log return value from core open */
    {
        FILE *log = fopen("/tmp/logs/859.txt", "a");
        if (log) {
            fprintf(log, "lfs_file_opencfg_: returned %d\n", err);
            fclose(log);
        }
    }

    LFS_TRACE("lfs_file_opencfg -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    /* Probe: log unlock and final return */
    {
        FILE *log = fopen("/tmp/logs/859.txt", "a");
        if (log) {
            fprintf(log, "LFS_UNLOCK called, EXIT lfs_file_opencfg returning %d\n", err);
            fclose(log);
        }
    }

    return err;
}
