int lfs_file_opencfg(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        /* Log lock failure so we can trace early exits */
        FILE *f = fopen("/tmp/logs/852.txt", "a");
        if (f) {
            fprintf(f, "lfs_file_opencfg: LFS_LOCK failed -> err=%d, lfs=%p, file=%p, path=%p\n",
                    err, (void*)lfs, (void*)file, (void*)path);
            fclose(f);
        }
        return err;
    }

    LFS_TRACE("lfs_file_opencfg(%p, %p, \"%s\", %x, %p {"
                 ".buffer=%p, .attrs=%p, .attr_count=%"PRIu32"})",
            (void*)lfs, (void*)file, path, (unsigned)flags,
            (void*)cfg, cfg->buffer, (void*)cfg->attrs, cfg->attr_count);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    /* Probe log: record entry state including address of the parameter 'path' */
    {
        FILE *f = fopen("/tmp/logs/852.txt", "a");
        if (f) {
            fprintf(f, "ENTRY: lfs_file_opencfg called\n");
            fprintf(f, "  lfs=%p, file=%p\n", (void*)lfs, (void*)file);
            fprintf(f, "  path_parameter_address=&path -> %p\n", (void*)&path);
            fprintf(f, "  path_value (pointer) -> %p\n", (void*)path);
            fprintf(f, "  path_string -> %s\n", path ? path : "(null)");
            fprintf(f, "  flags=0x%x, cfg=%p\n", (unsigned)flags, (void*)cfg);
            if (cfg) {
                fprintf(f, "  cfg->buffer=%p, cfg->attrs=%p, cfg->attr_count=%"PRIu32"\n",
                        cfg->buffer, (void*)cfg->attrs, cfg->attr_count);
            }
            fclose(f);
        }
    }

    /* Preserve original logic: call underlying implementation */
    err = lfs_file_opencfg_(lfs, file, path, flags, cfg);

    /* Probe log: record state after the call to detect if 'path' was modified */
    {
        FILE *f = fopen("/tmp/logs/852.txt", "a");
        if (f) {
            fprintf(f, "EXIT: lfs_file_opencfg returning\n");
            fprintf(f, "  returned err=%d\n", err);
            fprintf(f, "  path_parameter_address=&path -> %p\n", (void*)&path);
            fprintf(f, "  path_value (pointer) -> %p\n", (void*)path);
            fprintf(f, "  path_string -> %s\n", path ? path : "(null)");
            fclose(f);
        }
    }

    LFS_TRACE("lfs_file_opencfg -> %d", err);
    LFS_UNLOCK(lfs->cfg);
    return err;
}
