int lfs_file_opencfg(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
    /* Open a persistent log file for collecting control-flow/state info */
    FILE *logf = fopen("/tmp/logs/856.txt", "a");
    if (logf) {
        fprintf(logf, "ENTER lfs_file_opencfg: lfs=%p file=%p path=%s flags=%x cfg=%p\n",
                (void*)lfs, (void*)file, (path ? path : "(null)"), (unsigned)flags, (void*)cfg);
    }

    int err = LFS_LOCK(lfs->cfg);
    if (logf) {
        fprintf(logf, "After LFS_LOCK(lfs->cfg): err=%d\n", err);
        fprintf(logf, "Note: If LFS_LOCK expands to ((void)cfg, 0), err will always be 0 and the if (err) branch is unreachable.\n");
    }

    if (err) {
        /* This branch is expected to be unreachable when LFS_LOCK expands to a constant 0. */
        if (logf) {
            fprintf(logf, "UNEXPECTED: entered if (err) branch with err=%d - returning early\n", err);
            fclose(logf);
        }
        return err;
    }

    if (logf) {
        fprintf(logf, "Proceeding past LFS_LOCK check. About to call LFS_TRACE and continue normal flow.\n");
    }

    LFS_TRACE("lfs_file_opencfg(%p, %p, \"%s\", %x, %p {"
                 ".buffer=%p, .attrs=%p, .attr_count=%"PRIu32"})",
            (void*)lfs, (void*)file, path, (unsigned)flags,
            (void*)cfg, cfg->buffer, (void*)cfg->attrs, cfg->attr_count);
    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_opencfg_(lfs, file, path, flags, cfg);

    if (logf) {
        fprintf(logf, "lfs_file_opencfg_ returned %d\n", err);
    }

    LFS_TRACE("lfs_file_opencfg -> %d", err);
    LFS_UNLOCK(lfs->cfg);

    if (logf) {
        fprintf(logf, "Exiting lfs_file_opencfg: returning %d\n", err);
        fclose(logf);
    }
    return err;
}
