int lfs_file_opencfg(lfs_t *lfs, lfs_file_t *file,
        const char *path, int flags,
        const struct lfs_file_config *cfg) {
    /* Include standard IO headers here to ensure fopen/fprintf are declared
       even if the surrounding compilation unit doesn't include them. Placing
       the includes at the top of the function keeps any added declarations
       in a well-defined location. */
    #include <stdio.h>
    #include <stdint.h>

    int err = LFS_LOCK(lfs->cfg);
    if (err) {
        return err;
    }

    /* Original trace that contains the reported cast violation. Keep this
       unchanged so the original control flow / behavior is preserved. */
    LFS_TRACE("lfs_file_opencfg(%p, %p, \"%s\", %x, %p {"
                 ".buffer=%p, .attrs=%p, .attr_count=%"PRIu32"})",
            (void*)lfs, (void*)file, path, (unsigned)flags,
            (void*)cfg, cfg->buffer, (void*)cfg->attrs, cfg->attr_count);

    /* Supplemental logging to a file to capture control flow and the state
       relevant to the violation scenario. This writes an entry showing the
       pointer values and attribute counts so agents can reproduce/inspect
       the situation where the const qualifier may have been discarded. */
    do {
        FILE *log = fopen("/tmp/logs/855.txt", "a");
        if (!log) {
            break;
        }
        /* Entry header */
        fprintf(log, "ENTER lfs_file_opencfg\n");

        /* Basic context */
        fprintf(log, "  lfs=%p\n", (void*)lfs);
        fprintf(log, "  file=%p\n", (void*)file);
        fprintf(log, "  path=\"%s\"\n", path ? path : "(null)");
        fprintf(log, "  flags=0x%X\n", (unsigned)flags);

        /* Log the cfg pointer and the fact that the original code casts it to (void*) */
        fprintf(log, "  cfg(ptr)=%p (original LFS_TRACE used (void*)cfg)\n", (void*)cfg);

        /* Log cfg fields if cfg is non-NULL. We intentionally do not change
           original behavior -- the original code dereferences cfg in the
           LFS_TRACE above. Here we check for NULL only to avoid spurious
           additional crashes while producing logs. Typical test-suite
           inputs will not be NULL. */
        if (cfg) {
            fprintf(log, "  cfg->buffer=%p\n", cfg->buffer);
            fprintf(log, "  cfg->attrs=%p\n", (void*)cfg->attrs);
            /* Print attr_count as unsigned 32-bit number for readability */
            fprintf(log, "  cfg->attr_count=%" PRIu32 "\n", cfg->attr_count);
        } else {
            fprintf(log, "  cfg is NULL\n");
        }

        fclose(log);
    } while (0);

    LFS_ASSERT(!lfs_mlist_isopen(lfs->mlist, (struct lfs_mlist*)file));

    err = lfs_file_opencfg_(lfs, file, path, flags, cfg);

    LFS_TRACE("lfs_file_opencfg -> %d", err);

    /* Log exit/return value for full control-flow tracing. */
    do {
        FILE *log = fopen("/tmp/logs/855.txt", "a");
        if (!log) {
            break;
        }
        fprintf(log, "EXIT lfs_file_opencfg -> %d\n", err);
        fclose(log);
    } while (0);

    LFS_UNLOCK(lfs->cfg);
    return err;
}
